INSERT INTO `articles` (`id`, `subject`, `body`, `ndate`, `userid`, `resource`, `catid`, `keywords`) VALUES
(2, 'طراحی سایت رسپانسیو', '<p>طراحی سایت رسپانسیو (واکنش گرا - responsive ) را می توان یکی از ملزومات آینده طراحی وب سایتها دانست. امروزه تقریبا هر مشتری نسخه موبایل برای سایت خود را درخواست می کند. یک طرح برای تلفن های همراه طرح دیگری برای آی فون، طرحی برای آی پد، نوت بوک، تبلت و … . گاهی نسخه ای برای بعضی از دستگاه ها و گاهی نیز برای تمامی دستگاه ها. برای بسیاری از مشتریان ایجاد نسخه های متفاوت سایت برای رزولوشن ها و دستگاه های مختلف کاری غیر ممکن و یا حداقل غیر عملی است. و همچنین تکنولوژی به سرعت در حال پیشرفت است و معلوم نیست که چه دستگاه هایی با چه ابعادی در آینده به وسایل موجود افزوده شود. ما باید رنج از دست دادن بازدید کنندگان یک دستگاه را به نفع دستگاه دیگر را ببریم ؟ و یا روش دیگری وجود دارد؟</p>\r\n<p>طراحی سایت ریسپانسیو یا واکنش گرا (responsive) رویکردی است که باید سایتهای طراحی شده بر اساس رفتار کاربران و اندازه صفحه نمایش به صورت صحیح نمایش داده شود .در طراحی سایت رسپانسیو (رسپانسیو) باید سایتی طراحی شود که در تمامی رزولوشن ها، ابعاد و دستگاه ها به درستی نمایش داده شود.</p>\r\n<p>دلیل استفاده از طراحی سایت ریسپانسیو یا واکنش گرا چیست؟</p>\r\n<p>از آنجایی که سایت طراحی شده در تمامی دستگاه ها به درستی نمایش داده می شود، این از نظر سئو مهم است و به عبارتی سئو فرندلی است. و همچنین طراحی سایت واکنش گرا کاربر پسند است به این دلیل که کاربر با هر دستگاهی که وارد سایت شد می تواند با آسانی به اطلاعات دسترسی داشته باشد و این موجب رضایت مشتری می شود.</p>\r\n<p>طراحی سایت رسپانسیو تخصصی است که برای انجام آن دانش و تجربه کافی را می طلبد.</p>', '2013-10-10 12:29:18', 1, '-', 21, 'رسپانسیو-واکنش گرا-طراحی سایت-موبایل-تبلت'),
(3, 'چرا آیپد و آیفون سریع تر از دستگاه های اندرویدی هستند؟', '<p>حتما می دانید که آیفون و آیپد در تست سرعت پاسخگویی به دستورات لمسی، دستگاه های اندرویدی و ویندوزفونی نظیر گلکسی S4، لومیا ۹۲۸، اچ تی سی وان و ... را پشت سر گذاشته اند. این می تواند یکی از جاهایی باشد که اپل رقبا را حتی در آینه عقب هم نمی بیند. جالب اینجا است، محققین Agawi که این تست ها را ترتیب داده اند نیز از نتایج به دست آمده شگفت زده شده اند.</p>\r\n<div><strong>دلیل این فاصله چیست؟</strong></div>\r\n<div>شاید در واقع این نتایج چندان هم غافلگیر کننده نباشد. یکی از مهمترین مزیت های مدل کاری اپل این است که نرم افزار و سخت افزار را با هم و برای هم توسعه می دهد، چیزی که برای سایر شرکت ها میسر نیست و دستگاه های اندرویدی از بهینه سازی های هسته سیستم عامل بهره نمی برند.</div>\r\n<div>یکی از مشکلات مهم این است که اپلیکیشن های اندرویدی ابتدا بر اساس کدهای جاوا نوشته شده و سپس تبدیل می شوند؛ بنابراین به نوعی هیبریدی هستند. یعنی جاوایی که کاربران اینترنت آن را پر باگ و کند و پردردسر یافته اند، راهکاری است برای تولید اپلیکیشن های اندرویدی و این بر عملکرد و سرعت دستگاه ها تاثیر می گذارد.</div>\r\n<div>شاید به خاطر همین کندی ها و نواقص است که شرکت های وابسته به اندروید رو به تقلب در بنچمارک ها آورده اند.</div>\r\n<div><strong>فاصله کم نمی شود</strong></div>\r\n<div>اپل در حال مهاجرت به معماری ۶۴ بیتی است و در نظر داشته باشید، دستگاه های این شرکت که نتایج تست سرعت پاسخگویی اخیر با آنها انجام گرفته مربوط به یک سال قبل هستند! بنابراین در واقع، فاصله همین الآن هم بیشتر شده.</div>\r\n<div>با آیفون 5s و به زودی آیپد نسل ۵، اپلیکیشن ها نه فقط می توانند سریعتر اجرا شوند، بلکه سرعت پردازش صوتی نیز در کنار پردازش گرافیکی فوق العاده بیشتر خواهد شد. چند نسل که جلو برویم، تیم کوک دیگر یک آیپد هیبریدی را ایده ای ابلهانه نخواهد خواند، چون آن زمان قدرت پردازشی مناسب برای ساخت آن را خواهد داشت و نیازی به تکذیب نقشه های آینده ندارد.</div>\r\n<div><strong>خرید درست</strong></div>\r\n<div>اگر شما از اپلیکیشن هایی نظیر عکاسی و ویرایش عکس حرفه ای، اپلیکیشن های پخش موسیقی تعاملی و یا بازی ها استفاده زیادی می کنید، بهتر است آیفون یا آیپد بخرید زیرا iOS در این زمینه ها بسیار بهتر از اندروید است.</div>\r\n<div>در کل اگر قصد خرید گجتی در دامنه قیمت آیفون یا آیپد را دارید، هیچ محصولی به پای سرعت آنها نمی رسد. البته بدیهی است که در ازای به دست آوردن این سرعت، محدودیت های اکوسیستم اپل را نیز باید بپذیرید و این درست همانجایی است که رقبا اپل را در آینه عقب می بینند، یعنی قابلیت شخصی سازی سیستم عامل.</div>', '2013-10-13 10:54:46', 1, 'computerworld', 17, 'اپل-اندروید-گوگل-ios-Agawi-گلکسی-لومیا'),
(4, 'اینترنت اشیا چیست؟', '<p>تا پیش از این تصور اغلب ما این بود که تنها این انسان ها هستند که قرار است با ابزارهایی که در اختیار دارند توسط شبکه اینترنت به هم متصل باشند، و شخصا از قابلیت های آن بهره ببرند. اما بیش از یک دهه است که مفاهیم جدیدی شکل گرفته و در چند سال اخیر در قالب یک سری محصولات هوشمند به بازار راه پیدا کرده. اکنون در مورد ایده ای صحبت می کنیم که بر اساس آن هر شیء فیزیکی قادر خواهد بود با اتصال به اینترنت یا به کمک سایر ابزارهای ارتباطی، با سایر اشیا تعامل داشته باشد.</p>\r\n<p>اینترنت اشیا به این معنا است که بسیاری از وسایل روزمره مورد استفاده ما با اتصال به اینترنت، وظایف و اطلاعات خود را با هم و یا با انسان ها به اشتراک بگذارند. عبارت اینترنت اشیا، برای اولین بار در سال 1999 توسط کوین اشتون مورد استفاده قرار گرفت. او جهانی را توصیف کرد که در آن هر چیزی، از جمله اشیای بی جان، برای خود هویت دیجیتال داشته باشند و به کامپیوترها اجازه دهند آن ها را سازماندهی و مدیریت کنند.</p>\r\n<p>برای این که اشیا با همدیگر و یا با شبکه اینترنت تعامل داشته باشند، لازم بود به شیوه ای به هم متصل شوند. شاید از همان ابتدا که کوین اشتون عبارت اینترنت اشیا را پایه نهاد، استفاده از چیپ های شناسایی مبتنی بر فرکانس های رادیویی موسوم به RFID را در ذهن داشت. در این فناوری از چیپ های کوچک و ارزان قیمتی استفاده می شود و به کمک آن ها اطلاعات از طریق امواج رادیویی برای چیپ های گیرنده ارسال می گردد. چیپ های گیرنده، اطلاعات را تفسیر کرده و با اتصال به اینترنت امکان استفاده از آن اطلاعات را در گستره ای عظیم و برای کاربردهای مختلف فراهم می کنند.</p>\r\n<p>گرچه RFID هنوز با توجه به قابلیت های منحصر به فردش، به طور گسترده در جاهای مختلفی، نظیر درب های کنترل ورود و خروج و سیستم ترانزیت استفاده می شود، ولی فناوری های دیگری هم برای تحقق اینترنت اشیا وارد صحنه شده اند. برخی از آن ها مانند بارکدها از تکنیک های ساده تری استفاده می کنند و برخی از این فناوری هم مانند کدهای کیو آر، وای-فای و بلوتوث از تکنیک های جدیدتری بهره می برند.</p>\r\n<p>برای این که به درک بهتری از مفهوم اینترنت اشیا برسید، فروشگاهی را تصور کنید که دیگر به هیچ وجه نگران تمام شدن محصولاتش نیست، چون سیستم کنترل هوشمند انبار در هر لحظه از موجودی تمام محصولات مطلع است و حتی کار سفارش را نیز خود انجام می دهد. دنیایی را تصور کنید که در آن بتوان یخچال خود را طوری تنظیم کرد که وقتی تعداد تخم مرغ های درون آن به ۲ رسید، به صورت خودکار سفارش خرید تخم مرغ به فروشگاه نزدیک خانه تان ارسال شود، یا اینکه بتوانید روشن یا خاموش بودن اتو یا اجاق گازتان را به وسیله تلفن هوشمندتان از محل کار چک کنید.</p>\r\n<p>استارتاپ های بسیاری تاکنون در این عرصه قدم گذاشته اند. شرکت های نو پای نست و بلاتریکس تنها دو نمونه بسیار کوچک در این میان هستند. این شرکت ها با عرضه محصولاتی نظیر ترموستات هوشمند، سنسور هوشمند تشخیص دود و کلید های برق هوشمند در مسیر تحقق اینترنت اشیا گام برداشته اند. به گفته موسسه تحقیقاتی ABI تا سال ۲۰۲۰ بیش از ۳۰ میلیارد دستگاه به صورت وایرلس به بخشی از اینترنت اشیا پیوسته اند. شرکت سیسکو هم یک سیستم شمارنده داینامیک ابداع کرده که به کمک آن می تواند تعداد اشیای متصل را تخمین بزند. از آینده لذت ببرید.</p>', '2013-10-13 11:00:27', 1, 'wikipedia', 17, 'اینترنت-RFID-کوین اشتون-بلاتریکس-نست'),
(5, 'هنوز هم حتما باید درایوهای USB را safely remove کرد؟', '<p>هنگامی که بدون Safely Remove کردن کول دیسک تان آن را جدا می کنید، ویندوز عصبانی شده و پیغام های خطایی را نشان می دهد. اما چرا هنوز این مساله اینقدر مهم است؟ اگر هیچ گاه فلشهای یو اس بی و هارد دیسک های USB مان را safely Eject نکنیم، چه اتفاقی برای دستگاه های مان خواهد افتاد؟</p>\r\n<div>بگذارید اینگونه بگوییم که اغلب اوقات که عجله داریم، کول دیسک یا هاردمان را بدون اینکه روی آیکون safely remove کلیک کنیم و مراحل لازم را پشت سر بگذاریم، از کامپیوتر جدا می کنیم. و خوشبختانه کم پیش آمده که با مشکلی روبرو شویم.</div>\r\n<div>واقعا چه دلایلی پشت کلیک روی آیکون safely remove نهفته است؟ و آیا ممکن است زمانی به دلیل رفتار نامناسب، اطلاعات روی کول دیسک مان پاک شوند؟</div>\r\n<div><strong>و پاسخ:</strong></div>\r\n<div>بله هر اتفاقی ممکن است، و این بیشتر بستگی به این دارد که آیا هنگام جدا کردنش، کامپیوتر در حال خواندن و نوشتن اطلاعات روی این ابزارها است یا خیر؟</div>\r\n<div>هنگامی که درایو یو اس بی را به کامپیوتر وصل می کنید، اجازه خواندن و نوشتن بدون محدودیت داده ها را از روی آن می دهید که برخی از آنها هم کش می شوند.</div>\r\n<div>کش شدن اطلاعات باعث می شود داده ها بی درنگ روی درایو یو اس بی نوشته نشوند. بلکه ابتدا روی رم کامپیوتر نگهداری شده و سپس به محل اصلی منتقل می شوند. حال اگر هنگامی که هنوز فایلها به کول دیسک منقل نشده اند یا اینکه در حال نوشته شدن روی آن هستند، به صورت ناگهانی درایو را جدا کنید؛ آنگاه ممکن است نتیجه کار فایل های ناقص باشد.</div>\r\n<div>به هر حال ویندوز به صورت خودکار کش را برای ابزارهای USB غیرفعال می کند، مگر اینکه شما خودتان آن را فعال کرده باشید. لذا در صورت غیر فعال بودن کش می توان گفت اگر در حال خواندن و نوشتن روی کول دیسک نیستید، جدا کردن آن بدون Safely Remove هم تقریبا بدون خطر است.</div>\r\n<div>البته استفاده از آیکون Safely Remove Hardware نیز یک سطح امنیتی اضافی برای جلوگیری از نابودی اطلاعات تان است.</div>\r\n<div>اگر به گونه ای عمل کنیم که فایلها به درستی بسته شوند، می تواند باعث حفظ اطلاعات،اشاره گرها و شاخصهای اندازه فایل شود. هنگام نوشتن اطلاعات درون دیسک، کامپیوتر همیشه بافر را خالی نمی کند و تنها بخشی از داده ها نوشته می شوند. استفاده از رویه صحیح جداسازی می تواند باعث اطمینان از این موضوع شود که اطلاعات و اشاره گرها در وضعیت مناسبی قرار دارند.</div>', '2013-10-17 11:33:29', 1, 'Howtogeek', 19, 'کول دیسک-فلش-flash-cool disk-safely remove-usb-درایور');

