INSERT INTO `news` (`id`, `subject`, `image`, `body`, `ndate`, `userid`, `resource`, `catid`) VALUES
(9, 'دسترسی تمام مردم دنیا تا ۲۰۲۰ به اینترنت', '../newspics/internet.jpg', '<p><span>رئیس هیئت مدیره گوگل، اریک اشمیت در یک پیش گویی نسبتا عجیب و بزرگ درباره آینده و سرعت رشد اینترنت، دو روز پیش تویتی با این مضمون را ارسال کرد:</span><br /><span>امروزه به ازای هر شخص آنلاین، دو نفر وصل نیستند. اما در پایان این دهه، هر کسی روی زمین آنلاین خواهد بود.</span><br /><span>این گونه می توان گفت که آقای اشمیت معتقد است تنها تا ۷ سال دیگر حتی در بخش هایی از زمین که مردم اکنون به الکتریسیته هم دسترسی ندارند و برای بسیاری چیزهای حیاتی دیگر (همچون آب پاکیزه)‌ در تلاشند، اینترنت قابل دسترس خواهد بود.</span><br /><span>پس از این اظهار نظر، صفحه گوگل پلاس آقای اشمیت تبدیل به محل نزاع افرادی شد که این ایده بلند پروازانه وی را پسندیده اند یا اینکه اصلا اعتقادی به آن ندارند. برای مثال فردی به صورت کوتاه در پاسخ اشمیت نوشت: «می دانید این ایده چقدر غیرمنطقی است؟ هنوز یک میلیارد نفر از مردم دنیا هستند که به غذا دسترسی ندارند!»</span><br /><span>البته با توجه به پروژه های برخی شرکتها برای گسترش اینترنت در نقاط مختلف جهان، ایده آقای اشمیت را چندان هم نباید تخیلی و دور از ذهن دانست. برای مثال گوگل چندی است از پروژه ای به نام «گیک های بدون مرز» یا GEEKS Without Frontiers پشتیبانی می کند و هدف آن خلق تکنولوژی کم هزینه برای بردن اینترنت بیسیم به مکان هایی است که به صورت معمول استفاده از اینترنت با کابل کشی ممکن یا مقرون به صرفه نیست.</span><br /><span>یا اینکه سامسونگ در حال کار روی پروژه ای برای تبدیل کانتینرهای قدیمی و بدون استفاده به کلاس های درس مجهز به اینترنت است. این کانتینر ها قرار است با انرژی خورشیدی کار کنند. این کلاس های درس ویژه کودکان آفریقایی تولید می شوند و گفته می شود تا سال ۲۰۱۵ در اختیار ۲.۵ میلیون دانش آموز این قاره قرار خواهند گرفت.</span><br /><span>و این تنها دو مورد از پروژه های این چنینی هستند. اگر چه سخت است که باور کنیم تمام مردم دنیا تا سال ۲۰۲۰ به اینترنت دسترسی خواهند داشت، اما شاید بسیاری از آنها آنلاین شوند. نظر شما در این خصوص چیست؟</span></p>', '2013-08-16 09:44:16', 1, '-', 18),
(10, 'شاد ترین شرکت های دنیای فناوری', '../newspics/happy.jpg', '<p>شاید فکر کنید شرکتی که در زمینه نرم افزارهای محاسبه مالیات کار می کند، جای ملال آوری برای کار است ولی انگار درست بر عکس، Intuit شادترین شرکت دنیای فناوری برای کار است.<br />چه چیزی بهتر از کار در یکی از شرکت های دنیای تکنولوژی است؟ کار در یکی از شرکت های شاد دنیای تکنولوژی! سایت careerbliss فهرست شادترین شرکت ها و سازمان های دنیا را منتشر کرده و در این بین فهرست شادترین شرکت های تکنولوژی مربوط به کار و بار نارنجی می شود.<br />فکر می کنید کدام شرکت ها در این فهرست هستند؟ از نبودن برخی از معروف ترین ها در این فهرست، جا خواهید خورد.<br />AMD یکی از تولیدکنندگان بزرگ چیپ های پردازشی و گرافیکی در دنیای است که مقرش در سانی ویل کالیفرنیا واقع شده. پردازنده، مادر بورد و انواع قطعات کامپیوتری را تولید می کند و احتمالا در کامپیوتر فعلی شما هم، قطعه ای ساخت این شرکت یافت می شود. ولی فکرش را می کردید که یکی از شادترین شرکت های دره سیلیکون هم باشد؟</p>\r\n<p>امتیاز خوشحالی: 3.93 از 5<br />میانگین حقوق: 84.000 دلار</p>\r\n<p>9. HCL Technologies</p>\r\n<p>این شرکت در زمینه IT مشغول فعالیت و ارائه کننده خدمات برون سپاری، برون سپاری فرآیند های سازمانی و خدمات زیرساختی است. شرکتی از هند در بین خوشحال ترین شرکت های دنیا برای کار.</p>\r\n<p>امتیاز خوشحالی: 3.95 از 5<br />میانگین حقوق: 65.000 دلار</p>\r\n<p>8. Yahoo!</p>\r\n<p>یکی از تکیه گاه های با سابقه دنیای فناوری که بیش از همه به خاطر وب سایتش شناخته شده و این روزها مدیر عامل آن خانم ماریسا میر، جزو چهره های خبر ساز به شمار می رود. خانم میر اخیرا به مخالفت با کارمندانی پرداخته که در خانه وظایف خود را انجام می دهند. به نظر می رسد او اصرار دارد آنها در محیط شاد شرکت به کار روزانه خود بپردازند.</p>\r\n<p>امتیاز خوشحالی: 3.98 از 5<br />میانگین حقوق: 87.000 دلار</p>\r\n<p>7. Unisys</p>\r\n<p>این شرکت در هر جایی دستی دارد و انواع خدمات مربوط به فناوری اطلاعات را در دنیا ارائه می کند که بهبود کارایی و امنیت دیتا سنتر ها، کمک به پشتیبانی از مشتریان و مدرن سازی برنامه های کاربردی محیط کار، از جمله آنها است.</p>\r\n<p>امتیاز خوشحالی: 4 از 5<br />میانگین حقوق: 70.000 دلار</p>\r\n<p>6. Intel</p>\r\n<p>کیست که این نام را نشناسد؟! غول دنیای کامپیوترهای شخصی هر نوع پردازنده ای را برای دستگاه های مختلف تولید می کند و از ارزشمندترین شرکت های فعال در بخش نیمه هادی ها به شمار می رود. حالا هم که این رتبه خوب را در بین شرکت های شاد دره سیلیکون به خود اختصاص داده.</p>\r\n<p>امتیاز خوشحالی: 4.04 از 5<br />میانگین حقوق: 75.000 دلار</p>\r\n<p>5. EMC Corp</p>\r\n<p>EMC یکی از پیشتازان ذخیره داده در فضای ابری و مجازی سازی است و همان طور که با شرکت های کوچکتر در ارتباط است، خدمت رسان شرکت های بزرگ فهرست Fortune 500 هم هست.</p>\r\n<p>امتیاز خوشحالی: 4.07 از 5<br />میانگین حقوق: 87.000 دلار</p>\r\n<p>4. Google</p>\r\n<p>البته که غول دنیای جستجو باید یک جای شاد برای کار کردن باشد. غذای رایگان، اسکوتر سواری، زمین بازی و ... تازه اینها غیر از دفاتر کار راحت و زیبا است. مدیران گوگل به دنبال جذب افراد خاص هستند و آدم های خاص، محیط کار خاصی هم می طلبند.</p>\r\n<p>امتیاز خوشحالی: 4.10 از 5<br />میانگین حقوق: 72.000 دلار</p>\r\n<p>3. Avaya</p>\r\n<p>آوایا یکی از ارائه کنندگان سیستم های مخابراتی و ارتباطی برای شرکت های تجاری است که خدمات مربوط به توسعه شبکه ها و مراکز تماس را ارائه می کند. احتمالا در طول زندگی گذرتان به آن نخواهد افتاد، ولی بدانید که جای خوبی برای کار است.</p>\r\n<p>امتیاز خوشحالی: 4.12 از 5<br />میانگین حقوق: 87.000 دلار</p>\r\n<p>2. Texas Instruments</p>\r\n<p>یکی از بزرگترین تولیدکنندگان چیپ های مجتمع که شایعاتی درباره فروش کسب و کار تولید چیپ های OMAP آن به آمازون نیز چندی پیش شنیده شد، ید طولایی در تولید ماشین حساب های مهندسی دارد و علاوه بر پردازنده، به تولید انواع آمپلی فایر، رادیو، ساعت و تایمر، تبدیل کننده های دیجیتال و سنسورهای مختلف مشغول است.</p>\r\n<p>امتیاز خوشحالی: 4.14 از 5<br />میانگین حقوق: 74.000 دلار</p>\r\n<p>1. Intuit</p>\r\n<p>شاید فکر کنید شرکتی که در زمینه نرم افزارهای محاسبه مالیات کار می کند، جای ملال آوری برای کار است ولی انگار درست بر عکس، Intuit شادترین شرکت دنیای فناوری برای کار است.</p>\r\n<p>امتیاز خوشحالی: 4.27 از 5<br />میانگین حقوق: 77.000 دلار</p>\r\n<p>جای خالی شرکت هایی مثل اپل، آی بی ام، مایکروسافت، سامسونگ و فیسبوک در این فهرست به چشم می آید. البته می دانیم که در آنها بیشتر جو مهندسی بر پا است و خوشحالی، مهمترین مولفه تشخیص برتری محیط کار نیست چنانکه پیش از این در مطلبی به رتبه بندی شرکت های فناوری در بین 50 محل کار برتر سال 2013 پرداختیم و دیدیم که جایگاه آنها، چنین بود:</p>\r\n<p>۱- فیسبوک<br />۶- گوگل<br />۱۴- لینکداین<br />۱۵- راک اسپیس<br />۱۷- آکامای<br />۲۲- سیلزفورس دات کام<br />۳۱- اینتل<br />۳۳- رد هت<br />۳۴- اپل<br />۴۳- سپ</p>\r\n<p>ولی به هر حال رتبه بندی شادمانی محیط کار هم برای برخی افراد دارای اهمیت است. این طور نیست؟</p>', '2013-08-16 09:43:38', 1, 'سايت خبري عصر ايران', 19),
(11, 'مردی با دستان بیونیک قابل کنترل با اپلیکیشن آیفون', '../newspics/robot.jpg', '<p><span>تکنولوژی پزشکی مدرن هر روزه نوید بخش معجزات جدیدی است. و ابداعات و اختراعات آن با ورود به زندگی انسان های عادی می توانند بهبودهای خارق العاده ای باشند که شاید روزی جزو آرزوهای دست نیافتنی بودند. اما هر روز شاهد ورود بخش تازه ای از آنها به بازار و تبدیل افراد بیشتری به انسان-ماشین (Cyborg) هستیم.</span><br /><span>آغاز داستان امروزمان هم به سال ۲۰۰۸ برمی گردد. جیسون کاگر با بدشانسی تمام در یک تصادف روی خطوط نیرو سقوط کرد و با ۷۲۰۰ ولت شوک الکتریکی روبرو شد و متاسفانه هر دو دست خود را از دست داد. اما اکنون اینقدر خوش شانس است که توانسته دو دست جدید در اختیار بگیرد که توسط یک اپلیکیشن اختصاصی آیفون کنترل می شوند.</span><br /><span>i-Limb دست رباتیک فوق پیشرفته طراحی شده توسط موسسه انگلیسی توسعه ابزارهای پروتزی Touch Bionics است. ۵ انگشت این دست به طور مستقل حرکت می کنند و انگشت شست هم دارای حرکت و چرخش کامل است. علاوه بر این دست از مچ هم به صورت ۳۶۰ درجه امکان چرخش دارد.</span><br /><span>این دستان به اندازه کافی هوشمند هستند که به صورت خودکار کارهایی مانند لمس و گرفتن اجسامی چون لیوان و ابزارهای معمول خانگی را انجام دهند. اما برای راحتی بیشتر، کاگر یک اپلیکیشن اختصاصی آیفون هم در اختیار دارد و با استفاده از آن ۲۴ الگوی ویژه گرفتن پیچیده تر را هم می تواند برای دستانش برنامه ریزی کند.</span><br /><span>برای کار با این اپلیکیشن و انتخاب حالت های مختلف برنامه برای دستان، وی از یک قلم استایلوس معمولی استفاده می کند. البته دست های کاگر دقیقا همانند آنچه در ترمیناتور دیده اید، طبیعی به نظر نمی رسند. اما وی بسیار خوب آنها را اداره می کند و به خوبی از پس کارهایش بر می آید. و مطمئنا بسیار بسیار بهتر از هر دست پروتز پلاستیکی هستند که وی تا کنون استفاده می کرده است.</span><br /><span>به نظر شما چقدر تا تبدیل کامل انسان به یک موجود نیمه ماشینی و تحقق داستان های علمی-تخیلی همچون ترمیناتور، آی روبات و پلیس آهنی فاصله داریم؟</span></p>', '2013-08-17 09:40:19', 1, 'سايت خبري عصر ايران', 17),
(12, 'لحظه‌شماری هکرها برای بازنشستگی ویندوز ایکس پی', '../newspics/winxp2.jpg', '<p><span>کارشناسان امنیتی می‌گویند هکرهای خلاق تعداد زیادی از حفره‌های خطرناک ویندوز ایکس پی را شناسایی کرده و منتظر بازنشستگی این ویندوز برای سوءاستفاده‌های بعدی هستند.</span><br /><br /><span>به گزارش فارس به نقل از کامپیوترورلد، ویندوز ایکس پی در آوریل سال 2014 بازنشسته می‌شود و با توجه به تعداد قابل توجه کاربران این سیستم عامل انتظار می‌رود هکرها در آن زمان بدافزارهای مختلفی که بر مبنای مشکلات XP طراحی کرده‌اند را فعال کرده و در فضای مجازی منتشر کنند.</span><br />متخصصان امنیتی به کاربران هشدار داده‌اند که بهتر است هر چه سریع‌تر استفاده از ویندوز ایکس پی را متوقف کرده و به استفاده از سیستم عامل‌های جدیدتر مایکروسافت روی آورند. در حال حاضر حدود 33 درصد کاربران ویندوز از نسخه XP استفاده می‌کنند.<br />در حال حاضر متوسط قیمت بدافزارهای سوءاستفاده از ویندوز ایکس پی در بازار سیاه از 50 تا 150 هزار دلار در نوسان است که به نسبت دیگر نرم افزارها قیمت پایینی محسوب می‌شود.<br />مایکروسافت اعلام کرده که تا 8 آوریل 2014 وصله‌هایی برای رفع مشکلات امنیتی و آسیب پذیری های ویندوز XP عرضه اما پس از آن این روند را متوقف می‌کند. در همین حال بعید به نظر می رسد که کاربران فعلی ویندوز ایکس پی همگی تا آن زمان استفاده از سیستم عامل یاد شده را متوقف کنند.</p>', '2013-08-17 19:28:30', 2, 'سايت خبري عصر ايران', 17),
(13, 'پیشنهاد گوگل برای دامنه بدون نقطه رد شد', '../newspics/google.jpg', '<p><span>آیکان، سازمانی که در مورد مباحث ساختاری اینترنت حرف آخر را می‌زند، پیشنهاد گوگل در مورد استفاده از اسامی دامنه بدون نقطه برای تسهیل استفاده کاربران را رد کرد.</span><br /><br /><span>به گزارش فارس به نقل از پی سی مگ، نمایندگان شرکت گوگل چندی قبل با ارسال نامه‌ای برای سازمان آیکان از این نهاد خواستند تا سیستمی جدید و بدون نقطه برای اسامی دامنه خلق کند تا استفاده و وارد کردن دامنه‌ها از این طریق تسهیل شود. در صورت موافقت با این پیشنهاد کاربران برای ورود به سایت‌های اینترنتی تنها باید اسامی آنها را به طور متناوب تایپ می‌کردند.</span><br /><span>با اجرا شدن این طرح امکان داشت که کاربران با وارد کردن عبارت http://search/ وارد سایت گوگل شوند. این شرکت همچنین راه‌اندازی برخی آدرس‌های میان‌بر اینترنتی بدون نقطه را هم پیشنهاد کرده بود.</span><br /><span>در مقابل، آیکان با این پیشنهاد مخالفت کرده است. علت این مخالفت دغدغه‌های امنیتی و مشکلات مربوط به ثبات دامنه‌های اینترنتی عنوان شده است. در نامه آیکان در این زمینه آمده است: استفاده از آدرس‌های دامنه غیرمتعارف در اینترنت موجب سردرگمی کاربران می شود.</span><br /><span>برخی کارشناسان هم می‌گویند استفاده از اسامی دامنه بدون نقطه مشکلات فنی متعددی به وجود می‌آورد و از جمله ممکن است نحوه تبادل ایمیل را هم دچار مشکل فنی کند. زیرا این سبک نامگذاری با پروتکل تبادل ایمیل مانند SMTP سازگاری نخواهد داشت.</span></p>', '2013-08-18 20:57:17', 2, 'سایت خبری عصر ایران', 17),
(14, 'شنیده های اپلی آیفون، آیپد و آی او اس ۷', '../newspics/apple.jpg', '<p><span> این روزها شایعات فراوان و خسته کننده شده اند. ولی در این بین موارد به درد بخوری هم یافت می شود. بنابراین خیلی سریع و تیتروار به ۳ خبر اپلی می پردازیم.</span><br /><br /><span>خبر اول اینکه شاید مراسم ۱۹ شهریور اپل٬ علاوه بر معرفی آیفون 5S و 5C شاهد حضور آیپد نسل ۵ هم باشد. گویا همه چیز آماده است تا این سه محصول همراه با نسخه نهایی iOS 7 رونمایی شوند. احتمالا یک ماه بعد هم در مراسمی دیگر٬ شاهد رونمایی رسمی آیپد مینی به همراه آیپادها، مکبوک پروها و آیمک های سال ۲۰۱۳ خواهیم بود.</span><br />البته از گوشه و کنار در رد احتمال معرفی آیپد ۵ در کنار آیفون های جدید نیز خبرهایی رسیده. گویا نسخه مخصوص آیپد iOS 7 از برنامه عقب مانده و با این وصف احتمال دارد دو مراسم جداگانه را شاهد باشیم.<br /><br /><span>خبر دوم اینکه دکمه Home جنجالی آیفون 5S آن طور ها هم که تصور می شد محدب نخواهد بود و تنها اندکی با دکمه فعلی فرق خواهد داشت، طوری که از فاصله معمولی قابل تشخیص نیست و طراحی دستگاه را چندان تغییر نمی دهد.</span><br />طبق شنیده ها، اپل در نمونه های اولیه آیفون 5S حسگر اثر انگشت را قرار نداده٬ و ابتدا آن را در دستکاه های جداگانه ای تست کرده، به همین دلیل تا حالا عکسی از این قطعه لو نرفته و شاید تا خود مراسم هم پوشیده باقی بماند.<br /><br /><span>خبر سوم اینکه آیفون طلایی احتمالا چیزی شبیه تصویر فوق خواهد بود. اگرچه پیش تر انتظار می رفت آیفون 5S طلایی ترکیبی از رنگ طلایی و مشکی باشد ولی مارک گورمن از 9to5mac اطلاعاتی به دست آورده که احتمال دوم را تقویت می کند.</span><br />اپل بعد از آیفون ۵ مشکی که محصولی پر ایراد از کار در آمد٬ نیاز دارد دوباره یک محصول نمادین و جذاب رو کند و آیفون طلایی-سفید دقیقا می تواند نوری به درون تاریک خانه بتاباند.<br />شایدهم  آیفون 5S طلایی با هر دو ترکیب طلایی-سفید و طلایی-مشکی معرفی شد. اما باید اعتراف کرد گجت های تیره با iOS 7 همخوانی کمتری دارند.</p>', '2013-08-19 10:57:01', 2, 'سايت خبري عصر ايران', 17),
(15, 'خطر افشای رمز عبور با حفره امنیتی کروم', '../newspics/chrome.jpg', '<p><span>به تازگی حفره خطرناکی در مرورگر اینترنتی کروم شناسایی شده که به هر فردی امکان می‌دهد تا به کلمه عبور کاربران این مرورگر دسترسی پیدا کند.</span><br /><br /><span>به گزارش فارس به نقل از گاردین، این حفره به هر فردی که بخواهد از طریق رایانه دیگر افراد به مرورگر کروم دسترسی پیدا کند اجازه می‌دهد تا بتواند کلمات عبور ذخیره شده آنان را مشاهده کند. این نقص، کلمه‌های عبور ذخیره شده ایمیل‌ها، شبکه‌های اجتماعی و دیگر سایت‌ها که بر روی کروم ذخیره شده باشند را لو می‌دهد.</span><br />برای این کار کافی است کاربر به بخش تنظیمات مرورگر کروم مراجعه کرده و بدون نیاز به وارد کردن کلمه عبور یا اقدام خاص دیگری همه کلمات عبور سایت‌های مختلف را ببیند.<br />مدیران گوگل ضمن پذیرش وجود این مشکل می‌گویند در تلاش هستند تا مشکل یاد شده را برطرف کنند. در عین حال واکنش گوگل به وجود چنین مشکل بزرگی از سوی تیم برنرز لی مخترع وب ناامیدکننده توصیف شده است. کروم در حال حاضر میلیون‌ها کاربر دارد و برای کسب عنوان دومین مرورگر محبوب وب رقابت تنگاتنگی با فایرفاکس دارد.</p>', '2013-08-20 09:05:04', 2, 'سايت خبري عصر ايران', 17),
(16, 'تغییر لوگوی یاهو بعد از ۲۰ سال', '../newspics/yahoo.jpg', '<p><span>یاهو قصد دارد بعد از بیست سال لوگوی خود را تغییر دهد و قرار است ماه آینده از لوگوی جدید آن رونمایی شود.</span></p>\r\n<p><br />به گزارش فارس به نقل از تک نیوورلد، قرار است لوگوی جدید بازتابی از تحولات جدید یاهو بوده و تجارب جدید این شرکت را به نمایش بگذارد.<br />در حال حاضر یاهو برخی از لوگوهای پیشنهادی را که ممکن است به عنوان لوگوی نهایی یاهو انتخاب شود، در بخش های مختلف این سایت در معرض دید قرار داده است. به عنوان مثال، لوگوی بخش ایمیل و اخبار یاهو هم اکنون تغییر کرده و در هر بخش لوگویی مجزا به رنگ بنفش در دسترس قرار گرفته است، اما لوگوی صفحه اصلی این سایت هنوز دست نخورده باقی مانده است.<br />یاهو هدف از این کار را راحت تر شدن پذیریش تغییرات جدید برای کاربران خود عنوان کرده است. این اقدام تازه ترین ابتکار عمل مریسا میر مدیر عامل یاهو است که امیدوار است در رقابت با گوگل و مایکروسافت به رقابت های بیشتری دست یابد. </p>', '2013-08-15 11:28:09', 2, 'سايت خبري عصر ايران', 17),
(17, 'کشف یک کد خرابکار', '../newspics/orbit.jpg', '<p>محققان در یک برنامه مشهور دانلود تحت ویندوز یعنی Orbit Downloader، یک کد حمله انکار سرویس توزیع شده (DDoS) یافته‌اند که از راه دور به‌روزرسانی می‌شود.</p>\r\n<p> به گزارش ایسنا، این دانلود کننده مشهور حاوی یک برنامه DDoS است که بی صدا توسط وب‌سرورهای این شرکت برای هدایت حملات کنترل می‌شوند.</p>\r\n<p> به نظر می‌رسد که این تابع انکار سرویس توزیع شده مدتی است که در این برنامه وجود دارد. زمانی که برنامه orbitdm.exe اجرا می‌شود، مجموعه‌ای از ارتباطات را با سرورهایی در orbitdownloader.com برقرار می‌کند که در نتیجه، سیستم کلاینت به آرامی و از طریق HTTP، یک فایل Win32 PE DLL و یک فایل پیکربندی حاوی فهرستی از URLها و یک آدرس IP تصادفی به ازای هر URL را دانلود می‌کند.</p>\r\n<p> این برنامه و این فهرست برای هدایت یک حمله SYN flood یا موجی از درخواست‌های ارتباط HTTP بر روی پورت 80 و موجی از Datagramهای UDP برروی پورت 53 مورد استفاده قرار می‌گیرند. آدرس IP که در فایل پیکربندی به همراه URL قرار دارد، به عنوان آدرس منبع برای حمله استفاده می‌شود.</p>\r\n<p> تست‌های ESET حدود 12 نسخه از فایل DLL مشاهده کرده‌اند و محتویات فایل پیکربندی نیز اغلب تغییر کرده است. این نشان می‌دهد که شبکه انکار سرویس توزیع شده کاربران Orbit Downloader به‌طور فعال مدیریت می‌شود.</p>\r\n<p> ESET از کشف چنین حمله‌ای در چنین برنامه شناخته شده‌ای اظهار تعجب کرده است. این احتمال بعیدی است که وب‌سایت این شرکت توسط یک مهاجم بیرونی مورد سوء استفاده قرار گرفته باشد.</p>\r\n<p> بنا بر اعلام مرکز ماهر، نسخه آسیب‌پذیر این نرم‌افزار (4.1.1.18) همچنان برروی سایت این شرکت وجود دارد و URLهای مورد استفاده برای دانلود کد حمله نیز همچنان فعال هستند.</p>', '2013-08-26 12:05:07', 1, 'سایت خبری عصر ایران', 20),
(18, 'اضافه شدن ایران به لیست دانلود مجاز گوگل پلی', '../newspics/googleplay.jpg', '<p>گوگل دقایقی پیش در صفحه توسعه دهندگان اندروید در گوگل پلاس اعلام کرد که از امروز برنامه نویسان اندروید می توانند برای دانلود اپلیکیشن های رایگان شان، کشور ایران را نیز انتخاب کنند.گوگل در این پست کوتاه اشاره کرده که توسعه دهندگان اندروید برای این کار باید به کنسول توسعه دهندگان در بخش گوگل پلی مراجعه نموده و کشور ایران را نیز به لیست مجاز قابل دانلود برای اپلیکیشن شان اضافه کنند. اگر به خاطر داشته باشید مدتی پیش خبر از لغو برخی تحریم های رایانه ای برای کشور ایرانشد و در ادامه آن چند روز پیش شاهد حذف نام ایران از لیست کشورهای تحت تحریم اپلبودیم. حالا این گام جدیدی است که توسط گوگل برداشته شده و می بینیم که درهای گوگل پلی نیز کم کم برای کاربران ایرانی در حال باز شدن است. البته به نظر می رسد که هنوز امکان دانلود مستقیم از گوگل پلی برای کاربران ایرانی باز نشده، اما این خبر به این به این معنی است که کاربران ایرانی به زودی قادر خواهند بود که اپلیکیشن های رایگان را از گوگل پلی دانلود کنند. برای داشتن چنین امکانی لازم است که ابتدا توسعه دهندگان اپلیکشین ها، امکان دانلود اپلیکیشن شان را برای کشور ایران فعال کنند. و این امکان نیز از ساعاتی دیگر در اختیار آنها قرار خواهد گرفت. گوگل اسم ایران را نیز به لیست کشورهای مجاز برای دانلودهای رایگان گوگل پلی در اینجا هم اضافه نموده است. حالا باید منتظر بمانیم و ببینیم که دسترسی برای دانلود از گوگل پلی از چقدر دیگر برای کاربران ایرانی باز میشود؟ </p>', '2013-08-27 00:49:53', 1, 'سایت نارنجی', 18),
(19, 'یاهو طراحی ۷ سایت خود را تغییر داد', '../newspics/yahoo.jpg', '<p><span>در ادامه تغییراتی بزرگ و کوچکی که با آمدن «ماریسا مایر» طی ماه‌های گذشته در یاهو رخ داده است، این شرکت گام دیگری را در این راستا برداشت.</span><br /><br /><span>به گزارش خبرگزاری دانشجو، در ادامه تغییراتی بزرگ و کوچکی که با آمدن «ماریسا مایر» طی ماه‌های گذشته در یاهو رخ داده است، این شرکت گام دیگری را در این راستا برداشت. امروز یاهو طراحی 7 سایت زیر مجموعه خود را تغییر داد.</span><br />سایت‌های Yahoo Sports، Yahoo Movies، Yahoo Music، Yahoo TV، Yahoo omg!، Yahoo Games و Yahoo Weather در فهرست تغییرات رابط کاربری قرار گرفته‌اند.<br />یاهو با اعلام این خبر در وبلاگ رسمی خود گفته است که طراحی جدید این سایت‌ها، محتوا را به شکل بهتر و ساده‌تری به کاربران نمایش می‌دهد. یکی از تغییراتی که احتمالا کاربران فلیکر منتظر آن بودند، تغییر طراحی سایت آب و هوای یاهو بود.<br />از امروز نسخه وب این بخش مانند اپلیکیشن‌های آب و هوای یاهو برای اندروید و iOS، با استفاده از عکس شهر مورد نظر به عنوان تصویر پس زمینه که از بین عکس‌های ارسال شده کاربران در وب‌سایت فیلکر انتخاب شده است، نشان داده می‌شود.<br />یاهو می‌گوید طراحی جدید سایت‌هایش طی روزهای آتی برای همه کاربران جهانی در دسترس خواهد بود.</p>', '2013-09-01 13:09:28', 2, 'سايت خبري عصر ايران', 18),
(20, 'هک کردن کلمات عبور تصویری ویندوز ۸ ساده‌تر شد', '../newspics/win_8.jpg', '<p><span>قابلیت ویندوز ۸ برای ارائه کلمات عبور تصویری دارای مشکلاتی است که باعث می‌شود هک کردن آن به سادگی ممکن باشد.</span><br /><br /><span>به گزارش فارس به نقل از اینفورمیشن ویک، مایکروسافت همزمان با عرضه ویندوز 8 به کاربران امکان داد تا در کنار استفاده از کلمات عبور متنی از کلمات عبور تصویری که بر مبنای ژست‌های تصویری است هم استفاده کنند.</span><br />حال بررسی‌های کارشناسان امنیتی نشان می‌دهد سیستم یاد شده موسوم به picture gesture authentication (PGA) دارای مشکلاتی است و می‌توان آن را هک کرد. محققان دانشگاه ایالتی آریزونا و دانشگاه دلاویر می گویند کلمات عبور تصویری ویندوز 8 را به سادگی می‌توان هک کرد.<br />بررسی‌های این محققان در دو پایگاه داده به ترتیب به هک کردن 48 و 24 درصد کلمات عبور تصویری هدف گذاری شده که گروه مذکور هیچ اطلاعی از ماهیت آنها نداشته، انجامیده است. بر همین اساس 0.9 درصد از کلمات عبور با 5 حدس قابل شناسایی و هک کردن هستند.<br />این محققان از مایکروسافت خواسته‌اند اقدامات لازم را برای حل این مشکل انجام دهد، اما این شرکت هنوز واکنشی در این زمینه نشان نداده است.</p>', '2013-09-01 13:16:38', 2, 'سايت خبري عصر ايران', 20),
(21, 'توییتر وکیل برجسته‌اش را از دست داد', '../newspics/twitter.jpg', '<p><span>آلکس مک گیلیواری مشاور برجسته حقوقی توییتر که برای مدت‌های طولانی مدیریت امور حقوقی و قضایی این شرکت را بر عهده داشت، کار خود را ترک می‌کند.</span><br /><br /><span>به گزارش فارس به نقل از پی سی مگ، وی به مدت چهار سال این پست را بر عهده داشت و حال قرار است با توییتر خداحافظی کند.</span><br />وی در وبلاگ خود در این مورد نوشته است: من از این امتیاز برخوردار بودم که در یک دهه اخیر به نفع شرکت های بزرگ و کاربران آنها کار کنم و حجم زیادی از کار را بر عهده داشته باشم. حال می‌خواهم علاقه ام به جنبه های مختلف اینترنت را از جنبه‌های جدید و متفاوتی به کار بگیرم. بدون دلمشغولی ، دوستان و اعضای خانواده‌ام را ببینم و مدتی کار نکنم.<br />قرار است فرد دیگری له نام ویجایا گده به طور موقت جایگزین مک گیلیواری شود. وی با تیم های امنیتی و حقوقی توییتر همکاری خواهد کرد.<br />توییتر به علت تعداد زیاد کاربران و حضور انبوهی از سیاستمداران، شبکه‌ها و رسانه‌های خبری و چهره های مشهور مورد توجه هکرهاست و آنها بارها با نفوذ به حساب‌های کاربری این افراد و سازمان‌ها اخبار و اطلاعات دروغی را از قول آنها منتشر کرده‌اند و موجب مشکلات حقوقی گسترده برای توییتر شده‌اند.<br />مک گیلیواری تا سال 2009 در گوگل کار می کرد و در این سال به توییتر پیوست و این در زمانی بود که توییتر آرام آرام داشت مورد توجه قرار می گرفت. در زمان وی شیوه های جدیدی برای شناسایی هویت کاربران به کار گرفته شد و البته چندین بار توییتر دچار اختلال شد.</p>', '2013-09-03 12:35:21', 2, 'سايت خبري عصر ايران', 18),
(22, 'سرمایه‌گذاری جدید مایکروسافت برای زنده ماندن', '../newspics/microsoft.jpg', '<p><span>گزارش های خبری نشان می دهد احتمال سرمایه گذاری مایکروسافت در سایت Foursquare که خدمات اجتماعی بر مبنای موقعیت مکانی افراد ارائه می‌دهد، افزایش یافته است.</span><br /><br /><span>به گزارش فارس به نقل از یو اس ای تودی، مایکروسافت به علت تزلزل های مدیریتی اخیر حال و روز خوبی ندارد و ارزش سهام آن دیروز 0.3 درصد کاهش یافته است. ناکامی های استیو بالمر مدیر عامل فعلی مایکروسافت باعث شده تا وی ظرف 12 ماه آینده کنار رفته و از هم اکنون تلاش‌ها برای جایگزینی او آغاز شود.</span><br />Foursquare که کاربران زیادی دارد، برای جذب سرمایه مذاکراتی را با شرکت‌های مختلف آغاز کرده و امیدوار است از این طریق به کاربران همراه امکان دهد اطلاعات مورد نظر در مورد فروشگاه‌ها، رستوران‌ها و دیگر موقعیت های محلی را به طور آنی دریافت کنند.<br />انتظار می رود این سرمایه گذاری به تحولات اساسی در این شرکت منجر شود. این در حالی است که شرکت آمریکن اکسپرس هم به سرمایه گذاری در Foursquare اظهار علاقه کرده است. هر دو شرکت فعلا در مورد این خبرها سکوت کرده‌اند. </p>', '2013-09-03 12:40:19', 2, 'سايت خبري عصر ايران', 18),
(23, 'ویندوز 8.1 هم بازار رایانه‌های شخصی را نجات نمی‌دهد', '../newspics/win 8.1.jpg', '<p><span>امسال بازار رایانه‌های شخصی باز هم کم رونق‌تر خواهد شد و بررسی‌ها نشان می‌دهد ویندوز 8.1 شرکت مایکروسافت هم قادر نخواهد بود این بازار را نجات دهد.</span><br /><br /><span>به گزارش فارس به نقل از پی سی ورلد، استفاده گسترده از گوشی‌های هوشمند و تبلت برای اتصال به شبکه‌های ارتباطی و اینترنت به کاهش بی‌سابقه استفاده از رایانه‌های شخصی انجامیده و یکی از بازارهای پررونقی که این روزها توجهی به رایانه‌های شخصی ندارد، بازار چین است.</span><br />یافته‌های IDC نشان می دهد عرضه رایانه ‌ای شخصی در سال 2013 برابر با 9.7 درصد کاهش خواهد داشت. این رقم در سال 2012 برابر با 7.7 درصد خواهد بود.<br />سال گذشته پس از عرضه ویندوز 8 و عدم استقبال از آن بسیاری از تحلیگران عدم توجه و اقبال به ویندوز 8 را در این مساله دخیل دانستند. ویندوز 8.1 مایکروسافت قرار است در اوایل پاییز عرضه شود و برخی مشکلات ویندوز 8 و از جمله رابط کاربری لمسی گیج کننده آن در این نسخه برطرف شود.<br />گفتنی است در نیمه دوم سال 2013 میزان عرضه رایانه‌های شخصی به بازار 11.4 درصد کاهش یافته و کل تعداد رایانه های شخصی عرضه شده به بازار در این مدت تنها 75.6 میلیون واحد بوده است.</p>', '2013-09-03 12:42:48', 2, 'سايت خبري عصر ايران', 20),
(24, 'گوگل در عرضه مرورگر از موزیلا جلو زد', '../newspics/google.jpg', '<p><span>افزایش استفاده از مرورگرهای همراه باعث شده تا گوگل بتواند گوی سبقت را از رقیب قدیمی خود یعنی بنیاد موزیلا سازنده مرورگر فایرفاکس برباید.</span><br />به گزارش فارس به نقل از کامپیوترورلد، تازه‌ترین آمار منتشر شده از سوی Net Applications نشان می دهد در ماه آگوست 13.4 درصد از کاربران با استفاده از گوشی های همراه و تبلت به اینترنت متصل شدند. در مقابل 85.5 درصد از کاربران با استفاده از مرورگرهای رایانه های رومیزی و شخصی از اینترنت استفاده کردند.<br />این افزایش قابل توجه باعث شده تا مرورگر همراه گوگل کاربران بیشتری بیابد و در مجموع گوگل با پشت سر گذاردن فایرفاکس رتبه دوم را به خود اختصاص دهد. بر اساس بررسی های Net Applications حدود 17.5 درصد کاربران در حال حاضر از مرورگرهای همراه و رومیزی گوگل استفاده می کنند.<br />فایرفاکس هم در مجموع توانسته توجه 16.1 درصد از کاربران را به خود جلب کند. رتبه سوم هم به اپل رسیده که به علت اقبال فراوان به مرورگر همراه این شرکت توانسته 12.2 درصد از کاربران را از آن خود کند. نسخه همراه مرورگر سافاری بر روی آیفون ها و آی پدهای صدها میلیون نفر در سراسر جهان نصب شده است.<br />رتبه اول هم کماکان متعلق به مرورگر IE مایکروسافت است که 57.6 درصد از کل بازار را به خود اختصاص داده است، ولی اگر نتواند بر تعداد کاربران مرورگر همراه خود بی‌فزاید سهم قابل توجهی از بازار را از دست می دهد</p>', '2013-09-06 11:11:59', 2, 'سايت خبري عصر ايران', 18),
(25, 'یاهو با ایران خداحافظی کرد', '../newspics/yahoo.jpg', '<p><span>هر روز خبرهای جدیدی از تحریم های رایانه ای غرب که نشان از عدم مقابله با عزم راسخ کشور عزیزمان در فتح قله های دانش و فناوری و سربلندی را دارد شنیده می شود.</span></p>\r\n<p>تازه ترین خبر متعلق به شرکت یاهو می باشد.این شرکت در اقدامی عجیب نام ایران را از فهرست کشورهایی که هنگام ثبت نام پست الکترونیک دیده می شد حذف کرده است.</p>\r\n<p>برای ثبت نام و فعال سازی حساب کاربری نیز وارد کرد یک شماره تلفن همراه الزامی می باشد که این کار برای کاربران ایرانی غیر ممکن است.</p>\r\n<p>بدین ترتیب یاهو از ایران خداحافظی کرده و استفاده از آن برای کاربران ایرانی با مشکل مواجه خواهد بود. شنیده شده که سرویس مسنجر این شرکت موسوم به یاهو مسنجر نیز در ایران قطع خواهد شد.</p>\r\n<p>ذکر این نکته ضروریست که دو شرکت بزرگ گوگل و اپل پس از زیان های ناشی از تحریم ایران،نام کشورمان را از لیست تحریم خود خارج کردند.</p>', '2013-09-08 11:14:11', 1, 'افکار نیوز', 18),
(26, 'معرفی مودم بیسیم ASUS سازگار با محیط زیست', '../newspics/modem.jpg', '<p><span>مودم بیسیم ASUS DSL-N10S با ویژگی‎های سازگار با محیط زیست و کاهش 72 درصدی مصرف انرژی معرفی شد.</span><br />به گزارش سیتنا، مودم DSL-N10S با استفاده از فناوری‌های اختصاصی ایسوس در پردازنده شبکه بی سیم موفق به کاهش 72 درصدی مصرف انرژی و هزینه‌های ناشی از آن شده است.<br />این مودم از استاندارد شبکه بیسیم 802.11n پشتیبانی می نماید و ایسوس با بکارگیری پردازنده ECO-WiFi توانسته است در مصرف انرژی این مودم تا 72 درصد صرفه‌جویی کند. این مودم از شبکه بی‌سیم با استاندارد N150 پشتیبانی می‌کند و مجهز به یک پورت LAN است.<br />برای دسترسی آسان به شبکه و راه‌اندازی سریع یک ارتباط امن بی‌سیم دکمه WPS در DSL-N10S قرار داده شده و با استفاده همزمان از این دکمه می‌تواند سایر ابزارهای بی‌سیم را شناسایی کرده و ارتباط وایرلس را برقرار کند.<br />در پنل پشت این محصول یک درگاه اترنت RJ45 و یک پورت RJ11 برای ورود کانکتور تلفن در نظر گرفته شده است و در همین بخش می‌توان دکمه WPS را مشاهده کرد.<br />این مودم مجهز به یک آنتن بی‌سیم درونی است و در باند فرکانسی 2.4 گیگاهرتز فعالیت می‌کند. با پشتیبانی از استاندارد N150 پهنای باند این دستگاه به 150 مگابیت بر ثانیه می‌رسد و در بخش ADSL نیز با استاندارد ADSL2+ سازگار است. در این استاندارد میزان پهنای باند دانلود تا 24 مگابیت بر ثانیه و میزان پهنای باند آپلود یک مگابیت بر ثانیه است.<br />این مودم دارای قابلیت رمزگذاری روی داده‌های برای انتقال بی‌سیم است و از الگوریتم‌های WEP/TKIP/AES/WPA/WPA2 WPS پشتیبانی می‌کند. از دیگر ویژگی‌های این مدل می‌توان به سرور DHCP و فایروال اشاره کرد. ابعاد این محصول 120*120 میلی‌متر با ارتفاع 4 سانتی‌متر است.</p>', '2013-09-09 13:28:41', 2, 'سايت خبري عصر ايران', 17),
(27, 'نسل جدید پردازنده‌های اینتل مخصوص میکروسرورها', '../newspics/intel.jpg', '<p><span>شرکت اینتل برای بازار جهانی میکروسرورها تراشه جدیدی روانه بازار کرده است که سرعت و قدرت آن نسبت به مدل‌های کنونی بیشتر است.</span><br />به گزارش فارس به نقل از آیم‌اینفو تراشه جدید متناسب با سیستم‌های کم‌مصرف ساخته شده است و در عین حال امکان انجام فعالیت‌های پردازشی مختلف را فراهم می‌کند.<br />میکروسرورها، دستگاه‌هایی هستند که برای پردازش‌های رایانه‌ای مورد استفاده قرار می‌گیرند و با میزان کم انرژی قادر هستند به صورت دایم برای پردازش‌های رایانه‌ای استفاده شوند.<br />این محصول جدید مبتنی بر پردازنده‌های Atom اینتل ساخته شده و Avoton SoCs نام گرفته است و نسبت به سری پردازنده‌های Centerton مخصوص میکروسرورها توان بیشتری ارایه می‌دهد و در عین حال انرژی کمتری مصرف می‌کند.<br />پردازنده Avoton بخشی از سری محصولات C2000 اینتل در خانواده محصولات Avoton SoCs است. این پردازنده تنها برای میکروسرورها ساخته نشده است و شرکت اینتل امیدوار است از آن برای  سوئیچ‌ها، سیستم‌های امنیت شبکه و سیستم‌های ذخیره‌ساز اطلاعات نیز استفاده کند.</p>', '2013-09-09 13:37:46', 2, 'سايت خبري عصر ايران', 17);
INSERT INTO `news` (`id`, `subject`, `image`, `body`, `ndate`, `userid`, `resource`, `catid`) VALUES
(28, 'مصوبه جدید دولت برای انتقال از ویندوز به لینوکس', '../newspics/linux.png', '<p>بر اساس مصوبه جدید دولت، دستگاه‌های اجرایی کشور باید در مدت شش ماه طرح مهاجرت خود  برای جایگزین کردن سیستم‌عامل ویندوز مایکروسافت و همه برنامه‌های ویندوزی با سیستم عامل گنو/لینوکس  را تدوین و تاییده آن را از  سازمان فناوری‌اطلاعات ایران اخذ کنند.<br />به گزارش پایگاه اطلاع رسانی شبکه خبر، آقای مرتضی شهامی مدیر کل ارتباطات و فناوری اطلاعات استان زنجان اعلام کرده است: »دستگاه‌های دولتی شش‌ماه از ابلاغ این مصوبه فرصت دارند تا برنامه خود را تدوین و به اداره کل ارتباطات و فناوری‌اطلاعات تحویل دهند.«</p>\r\n<p>آقای شهامی همچنین افزود: هم اکنون تمامی ادارات کل ارتباطات و فناوری‌اطلاعات در سطح کشور در همه سیستم‌های خود از لینوکس استفاده می‌کنند و دیگر ویندوز در این مجموعه‌ها مورد استفاده قرار نمی گیرد.</p>\r\n<p>این مصوبه دولت به این معنی است که همه دستگاه‌های اجرایی کشور موظفند به جای ویندوز مایکروسافت از سیستم‌عامل گنو/لینوکس و نرم‌افزارهای قابل نصب بروی این سیستم عامل استفاده کنند.</p>\r\n<p>سیستم عامل لینوکس سیستم‌عاملی است که به صورت نرم‌افزار آزاد منتشر می‌شود. هسته این سیتسم‌عامل در سال ۱۹۹۱ بوسیله لینوس توراالز ایجاد شد و سایر بزنامه‌های این سیستم‌ بوسیله سایر برنامه‌نویس‌ها و عموما با حمایت بنیاد گنو توسعه داده می‌شود  نرم‌افزار آزاد به نرم‌افزاری گفته می‌شود که کاربران حق دارند آن را به صورت آزادانه اجرا، کپی و مجددا منتشر کنند، همچنین کاربران حق دارند کدهای برنامه‌های آزاد را تغییر و بهبود دهند. نرم‌افزار آزاد لزوما رایگان نیست و برنامه نویس می‌تواند برنامه خود را بفروشد.</p>', '2013-09-11 10:13:51', 1, '-', 20),
(29, 'اینتل دنیای نرم‌افزار و سخت‌افزار را به‌روز کرد', '../newspics/intel.jpg', '<p><span>این شرکت در معرفی نسل جدید محصولات خود از پردازنده چندهسته‌ای SoC مبتنی بر فناوری 22 نانومتری، رایانه همراه Chromebook مبتنی بر معماری Haswell و چهارمین نسل از پردازنده‌های Core vPro برای کاربران تجاری رونمایی کرد.</span><br />شرکت اینتل از آغاز طرح جدیدی خبر داد که به موجب آن نسل جدید فناوری‌ سخت‌افزاری و نرم‌افزاری خود را روانه بازار می‌کند.<br />به گزارش فارس به نقل از سلولار – نیوز، این شرکت در معرفی نسل جدید محصولات خود از پردازنده چندهسته‌ای SoC مبتنی بر فناوری 22 نانومتری مخصوص رایانه‌های لوحی، رایانه همراه Chromebook مبتنی بر معماری Haswell و چهارمین نسل از پردازنده‌های Core vPro برای کاربران تجاری رونمایی کرد.<br />دیگر فناوری‌های جدید اینتل شامل ظرفیت‌های افزوده شده به سرویس‌های پردازش ابری و سری ابزارها و نرم‌افزارهای جدید این شرکت می‌شدند که با انواع فضاهای پلتفورمی از جمله اندروید، کروم Chrome و ویندوز سازگاری داشتند.<br />دوگ فیشر( Doug Fisher) مدیر گروه نرم‌افزار و خدمات اینتل در این باره گفت: «از آنجایی که ابزارهای همراه بین کاربران جهانی شخصی و شخصی‌تر می‌شوند، اینتل در این زمینه نوآوری‌های فراوانی عرضه کرده است، تا مبتنی بر آن‌ها بتواند نسل جدید خدمات خود را ارایه دهد و امکان تجربه جدید کاربران شخصی و سازمانی در دنیای فناوری را فراهم آورد».<br />فیشر همچنین خاطرنشان کرد که شرکت اینتل در نسل جدید پردازنده‌های مرکزی 64 بیتی خود از سیستم‌عامل اندروید پشتیبانی می‌کند و این امکان را برای توسعه‌دهندگان نرم‌افزاری فراهم می‌آورد، تا با سیستم‌های حافظه‌ای بیش از 4 گیگابایت بتوانند نسل جدید دستگاه‌های الکترونیکی را طراحی و تولید کنند.</p>', '2013-09-15 14:11:57', 2, 'سايت خبري عصر ايران', 20),
(30, 'ضربان قلب جایگزین رمز عبور می‌شود', '../newspics/ring.jpg', '<p><span>محققان کانادایی دستبندی طراحی کرده‌اند که از ریتم منحصر به فرد ضربان قلب بعنوان رمز عبور رایانه استفاده می‌کند.</span><br />استفاده از رمز عبورهای بیومتریک مانند اثر انگشت، اسکن عنبیه یا ریتم منحصر به فرد ضربان قلب بسرعت در حال جایگزین شدن با کلمات عبور معمولی هستند<br />دستبند Nymi با نظارت بر ضربان قلب، جدیدترین فناوری عرضه شده در حوزه رمز عبورهای بیومتریک محسوب می شود.<br />کاربر با بستن این دستبند به دور مچ، از الگوی منحصر به فرد ضربان قلب برای تأیید هویت و قفل گشایی از تلفن همراه هوشمند، تبلت یا رایانه شخصی استفاده می‌کند.<br />شناسایی کاربر توسط گجت های مختلف تا زمانیکه این دستبند دور مچ قرار داشته باشد، ادامه پیدا می کند؛ اما درصورتیکه کاربر دیگری دستبند را استفاده کند، گجت با تشخیص ضربان قلب متفاوت بسرعت قفل می شود.<br />این دستبند مجهز به حسگر الکترودیاگرام و آشکارساز حرکت است که امکان کنترل حرکات دست را فراهم می کند؛ نرم افزار این گجت هوشمند برای استفاده در سیستم عامل اندروید، ویندوز، Macs‌ و iOS طراحی شده است.<br />مراحل آزمایش نمونه اولیه دستبند ضربان قلب در حال انجام است و پیش بینی می شود که این محصول در بهار 2014 میلادی به قیمت 99 دلار به بازار عرضه شود. </p>', '2013-09-15 14:16:53', 2, 'ايسنا', 17),
(31, 'توصیه مایکروسافت به کاربران ویندوز ایکس پی به ویندوز 8.1 مهاجرت نکنید', '../newspics/win 8.1.jpg', '<p><span>مایکروسافت به کاربران خانگی و حرفه ای ویندوز ایکس پی توصیه کرده تا به ویندوز 8.1 مهاجرت نکنند، زیرا رایانه های قدیمی با این سیستم عامل جدید سازگار نیست.</span><br />به گزارش فارس به نقل از آی دی جی، قرار است سیستم عامل ویندوز 8.1 با مدتی تاخیر در 18 اکتبر روانه بازار شود، اما مایکروسافت رسما اعلام کرده که ویندوز 8.1 برای نصب بر روی رایانه‌هایی که ویندوز XP یا ویستا بر روی آنها نصب شده مناسب نیستند.<br />در حال حاضر پیشرفته ترین ویندوزی که کاربران ویندوز XP می توانند به آن مهاجرت کنند ویندوز 7 است. ویندوز XP حدود 12 سال قبل روانه بازار شده و مدتهاست که مایکروسافت برای کاهش تعداد کاربران آن و وادار کردن افراد به مهاجرت به نسخه های جدیدتر ویندوز تلاش می کند، اما هنوز در این زمینه موفقیت کامل نداشته است.<br />این شرکت هشدار داده که آخرین مجموعه از وصله های به روزرسان امنیتی برای ویندوز XP را در 8 آوریل سال 2014 عرضه خواهد کرد و از آن پس در برابر مشکلات و آسیب پذیری های این سیستم عامل هیچ مسئولیتی نخواهد داشت. برخی کاربران ویندوز XP نسبت به این سیاست مایکروسافت اعتراض کرده اند.</p>', '2013-09-24 17:59:44', 1, 'سايت خبري عصر ايران', 20),
(32, 'کند شدن جیمیل نیمی از کاربران را به دردسر انداخت', '../newspics/gmail.jpg', '<p><span>از روز گذشته اختلال در سرویس پست الکترونیک جیمیل، باعث شد حدود نیمی از کاربران خدمات یاد شده در سراسر جهان دچار مشکل شوند.</span><br />به گزارش فارس به نقل از بوستون هرالد، مشکلات یاد شده باعث شد ایمیل های بسیاری از کاربران با تاخیر به مقصد برسد و بارگذاری فایل های ضمیمه ایمیل ها هم با مشکلاتی انجام شود.<br />گوگل ساعت 10:30 صبح روز گذشته به وقت محلی وقوع این مشکل را پذیرفت و می گوید از آن زمان تاکنون برای رفع آن در تلاش است. سرانجام در ساعت 2 دو صبح امروز به وقت محلی این شرکت اعلام کرد که کمتر از 50 درصد از کاربران هم اکنون با این مشکل مواجه هستند و رسیدگی به مساله در جریان است.<br />جیمیل هم اکنون بیش از 425 میلیون نفر کاربر دارد و با این حساب بیش از 212 میلیون نفر از مشترکان این خدمات نتوانسته اند به ایمیل های خود دسترسی پیدا کنند. گوگل در آخرین اطلاعاتی که در این زمینه ارائه کرده وعده داده که مشکل یاد شده امروز به طور کامل مرتفع شود.<br />علاوه بر جیمیل، خدمات اسناد گوگل و همین طور سرویس طراحی پرزنتیشن این شرکت هم از ساعت 10:30 صبح دیروز به وقت محلی تا ساعت 4 بعداز ظهر دچار مشکل بودند. گوگل از ارائه هرگونه توضیحی در مورد علل فنی وقوع این مشکل خودداری کرده است.<br />اهمیت این اختلال در آن است که نه تنها کاربران عادی، بلکه صاحبان مشاغل، نهادهای دولتی و مدارس طرف قرارداد با گوگل را هم دچار مشکل نمود.</p>', '2013-09-25 13:39:36', 2, 'سايت خبري عصر ايران', 18),
(33, 'سیستم تشخیص اثر انگشت در آیفون جدید هک شد', '../newspics/finger.jpg', '<p><span>گروهی از محققان آلمانی موفق شدند اسکنر مخصوص تشخیص اثر انگشت که در آخرین مدل گوشی هوشمند اپل نصب شده است را هک کنند.</span><br />به گزارش فارس به نقل از سلولار – نیوز، شرکت اپل چندی پیش مدل جدید گوشی هوشمند آیفون را همراه با اسکنر مخصوص تشخیص اثر انگشت معرفی کرد تا امنیت این محصول را نسبت به مدل‌های قبلی افزایش دهد. با این وجود، کارشناسان اعلام کردند که به کمک ابزارهای روزمره موفق شدند این اسکنر امنیتی را دور بزنند.<br />سطح شیشه‌ای هوشمندی که در مدل جدید گوشی هوشمند آیفون کار گذاشته شده است از اثر انگشت کاربری که از این گوشی استفاده می‌کند عکس می‌گیرد. این سیستم به‌گونه‌ای ساخته شده است که می‌توان یک اثر انگشت تقلبی را با آن عکس گرفت و از طریق ابزار TouchID وارد گوشی هوشمند iPhone 5S شد.<br />هکر این گوشی که خود را با نام مستعار "استاربوگ"(Starbug) معرفی کرده است توضیح داد: «در واقع حسگر اپل نسبت به حسگرهای قبلی که در این زمینه استفاده می‌شدند کیفیت بالاتری دارد. بنابراین، ما فقط سعی کردیم کیفیت عکسی که از اثر انگشتمان گرفته می‌شود را افزایش دهیم تا به راحتی وارد گوشی شویم.»<br />او در ادامه گفت: «همان طور که ما برای سال‌های طولانی گفتیم، اثر انگشت نمی‌تواند برای افزایش امنیت دستگاه‌های الکترونیکی مورد استفاده قرار گیرد. شما اثر انگشت خود را در هر مکانی جا می‌گذارید،‌ و البته این مسئله بسیار آسان است که بتوان از اثر انگشت به‌جا مانده از شما یک پرینت گرفت و آن را برای باز کردن گوشی استفاده کرد».</p>', '2013-09-25 13:47:12', 2, 'سايت خبري عصر ايران', 18),
(34, 'سامسونگ اول از همه جلو می رود، گوگل تست کرده، اپل تصحیح می کند!', '../newspics/google-apple.jpg', '<p>اگر به بازار فناوری های همراه نگاهی بیاندازیم، متوجه می شویم که سه بازیگر بزرگ فعال در این عرصه، یعنی سامسونگ، گوگل و اپل، هر یک به شیوه منحصر به فرد خود گام بر می دارند. سامسونگ در این میان نقش پیشرو را دارد، عجولانه تر از بقیه حرکت می کند و با سرعت تمام، حرف ها و ایده های خود را عملی می سازد. گوگل نقش آزمایشگر عاقل را بازی می کند، اپل هم خود را از جار و جنجال ها دور نگه داشته، بهترین ایده های بازار گرفته و تصحیح کرده و سپس با یک محصول پرقدرت در بازار حضور پیدا می کند.</p>\r\n<div>نگاهی به آهنگ بازار و هیاهوی تبلیغاتی این سه شرکت بزرگ، کافی است، تا بتوانیم به درک صحیحی از ریتم حرکت آن ها برسیم. در ادامه با ما همراه باشید تا به طور جداگانه نگاهی اجمالی به رویکرد این سه شرکت بیاندازیم.</div>\r\n<p><strong>سامسونگ پیشرو و در عین حال عجول</strong></p>\r\n<div>برای بررسی رویکرد سامسونگ لازم نیست کل تاریخچه آن را رصد کنیم. کافیست به همین آخرین محصول این شرکت یعنی گلکسی راند نگاهی بیاندازیم. این تلفن هوشمند با نمایشگر خمیده اش توجه بسیاری را به خود جلب کرده. شاید خیلی ها شروع به بررسی زیر و بم این نوآوری سامسونگ کرده اند. اما به نظر می رسد که برای سامسونگ "اولین بودن" از همه چیز مهمتر است. گرچه سامسونگ همواره متهم بوده که از محصولات سایر رقبا کپی برداری می کند، اما هرگز نمی توان از بی پروایی و جسارت مثال زدنی این شرکت به راحتی گذشت.<br>اگر نگاهی به آخرین نشست مطبوعاتی سامسونگ بیاندازیم، سه عبارت کلیدی را در توصیف گلکسی راند خواهیم دید: "اولین در جهان"، "مبتکر نمایشگرهای موبایل" و "پیشتاز". شاید این گفتمان سامسونگ بیش از همه چیز، عطش این شرکت برای اول بودن را نشان می دهد.<br>گلکسی راند تنها محصولی نیست که سامسونگ از آن برای نشان دادن پیشگام بودن خود استفاده تبلیغاتی کرده، ساعت هوشمند گلکسی گیر هم همین کارکرد را برای سامسونگ داشته. شاید این محصول، اولین ساعتی نباشد که پسوند هوشمند را برای خود اختیار کرده، اما صراحتا می توان گفت که تا این تاریخ، گلکسی گیر اولین محصول بلند پروازانه موجود در رده ساعت های هوشمند است.</div>\r\n<div> <strong>گوگل عاشق تست کردن و نسخه های بتا</strong></div>\r\n<div> گوگل هم به مانند سامسونگ علاقه زیادی به اول بودن دارد، اما به شیوه ای کاملا متفاوت. کروم بوک پیکسل با سیستم عامل کروم، و یا گوگل گلس، محصولاتی هستند که گوگل به کمک آن ها توانسته جسارت خود را برای ارائه محصولات نو نشان دهد. اما تلاش های گوگل یک جنبه دیگر هم دارند. گوگل گرایش شدیدی به تهیه نسخه های بتا و محصولات آزمایشی دارد.</div>\r\n<div>این رویکرد گوگل، هم در محصولات نرم افزاری این شرکت و هم در دستگاه ها و گجت های آن مشاهده می شود. گوگل تلاش می کند در یک روند نسبتا طولانی محصولات خود را از آزمایش های مختلف گذرانده و به تکامل آن ها کمک کند. برعکس، سامسونگ همانطور که گفته شد، یک دفعه سراغ محصول نهایی می رود و اول بودن در بازار را نشانه برتری می داند.<br>گوگل روی محصولاتی برچسب بتا می زند که بسیاری از شرکت های دیگر سال ها محصولات مشابه آن را ساخته اند و توسعه داده اند. از این دست محصولات می توان به کروم بوک و دستگاه های نکسوس اشاره کرد. شاید هیچ شرکت دیگری به اندازه گوگل برای فرایند تکاملی محصولات ارزش قائل نیست.</div>\r\n<div><strong>اپل نظاره گر زیرک و حساس بروی نسخه های نهایی</strong></div>\r\n<div>اپل را می توان صبورترین شرکت در میان رقبا دانست. اپل از لحاظ اهمیتی که به تحقیقات و توسعه می دهد، به گوگل شباهت دارد، ولی باید ذکر کرد که اپل این کار را با سر و صدای کم و پشت درهای بسته انجام می دهد. این شرکت با زیرکی، شکست ها و پیروزی های رقبا را به نظاره می نشیند، و سپس گام بعدی خود را مشخص می کند.<br>به هیچ وجه نمی توان نوآوری های اپل را به خاطر رویکرد آن زیر سوال برد. نوآوری های این شرکت از بسیاری از جهات تاثیر گذاری بیشتری نسبت به ابداعات رقبا دارند؛ اسکنر اثر انگشت اپل را در نظر بگیرید، شاید این فناوری به کار رفته در آیفون جدید اپل، خیلی هم جدید نباشد، اما چارچوبی که اپل در آن از این فناوری استفاده کرده و کاربردی که برای آن تعریف کرده، باعث شده این مشخصه، علاقه مندان زیادی را به سوی خود جلب کند.<br>نمی توان به صراحت گفت کدام یک از این غول های حوزه فناوری های دیجیتال، راه درست را پیش گرفته اند. هر کدام از آن ها نقش بزرگی را ایفا می کنند: سامسونگ با جسارتش بازار را به حرکت رو به جلو فرا می خواند، گوگل در مقیاس های کلان به تحقیق و توسعه در مورد محصولاتش می پردازد، اپل هم از تجربیات دیگران بهره برده و تلاش می کند کمال گرایی خود را در محصولاتش تزریق کرده و به مردم آن چیزی را بدهد که آن ها واقعا خواستار آن هستند.</div>', '2013-10-12 17:30:23', 1, 'نارنجی', 19),
(35, 'نا امن بودن ارتباطات اپلیکیشن WhatsApp!!!', '../newspics/wahtsapp.jpg', '<div id="id_52595ca50ca896b59581292" class="text_exposed_root text_exposed">باز هم اپلیکیشن پیام رسان WhatsApp خبرساز شد و در صدر خبرهای امنیتی دنیای وب قرار گرفت.مشکل این است که WhatsApp سابقه خوبی در زمینه حفظ حریم خصوصی کاربرانش ندارد <br>مسئله شیوه رمزگذاری مورد استفاده در این اپلیکیشن بود. همان طور که می دانید WhatsApp به اطلاعاتی نظیر شماره هویت بین المللی تجهیزات موبایل یا IMEI Mobile و نیز Mack address شبکه ای که با آن به اینترنت متصل شده اید دسترسی دارد. IMEI مشخصه ای است که توسط سازنده به دستگاه تلفن شما اختصاص داده می شود.<br>اما مسئله مشکل ساز این است که اپلیکیشن WhatsApp از مشخصات گفته شده برای رمزگذاری استفاده می کند، درست مثل اینکه شما برای رمز عبورتان شماره شناسنامه خود را قرار دهید؛ در ضمن اگر کسی از آن باخبر شود راهی برای تغییر رمز وجود ندارد.<br>حالا مطلب دیگری دوباره امنیت این اپلیکیشن را با سوال مواجه کرده؛ این شرکت ادعا دارد که تمام ارتباطات تلفنی کاربرانش را رمزگذاری می کند؛ اما با بررسی های کارشناسان حوزه امنیت اطلاعات مشخص شده که خیلی هم این موضوع صحت ندارد.<br>مسئله رمزگذاری ارتباط در اپلیکیشنی که تمام موجودیت خود را بر پایه آن بنا نهاده بسیار خطرناک و تاسف آور است. از این بدتر رویه کتمانی است که WhatsApp در قبال آن در پیش گرفته و ظاهراً فعلاً قصدی برای اصلاح مشکل واضح خود ندارد.</div>', '2013-10-12 18:02:21', 1, '-', 18),
(36, 'آپدیت ویندوز ۸.۱ از ساعت ۱۴:۳۰ پنج شنبه در دسترس قرار گرفت', '../newspics/windows_81.jpg', '<p>ویندوز ۸.۱ که در تیر ماه معرفی شد، از ساعت ۱۴:۳۰ روز پنج شنبه به صورت یک آپدیت برای دستگاه های برخوردار از ویندوز ۸ عرضه شد و از جمعه نیز با قیمت ۱۲۰ دلار به صورت جداگانه به فروش خواهد رسید.</p>\r\n<p>مایکروسافت این نسخه از سیستم عامل خود را یک آپدیت نامیده و نه آپگرید، بنابراین انتظار زیر و رو شدن ویندوز ۸ را نداشته باشید. در عوض، دکمه استارت و شخصی سازی ها و بهینه سازی های زیادی از راه خواهند رسید.</p>\r\n<div>افرادی که هم اکنون ویندوز ۸ را روی دستگاه خود نصب کرده اند، ویندوز ۸.۱ را به صورت یک آپدیت اختیاری مشاهده خواهند کرد و کسانی هم که از قبل نسخه پیش نمایش ویندوز ۸.۱ را نصب کرده اند، همین طور. نسخه جدید سیستم عامل به صورت یک آپدیت در ویندوز استور به نمایش در خواهد آمد.</div>', '2013-10-17 11:41:26', 1, 'نارنجی', 20),
(37, 'ربات شکلاتی دست تکان می دهد؛ معرفی اندروید ۴.۴ کیتکت نزدیک است', '../newspics/AndroidKitKat.jpg', '<div>گوگل با ارسال عکس بالا در گوگل پلاس و دعوت همه به رقص باعث شده رسانه ها مطمئن شوند که همین روزها، بالاخره اندروید ۴.۴ با نکسوس ۵ از راه خواهد رسید.</div>\r\n<div>تاریخ های مختلفی برای معرفی نسخه جدید اندروید ذکر شده و گفته می شود تعطیلی بخش هایی از دولت امریکا باعث شده دریافت مجوزهای لازم برای معرفی نکسوس ۵ با مشکل مواجه شود.</div>\r\n<div>امروز، فردا، یا شاید تاریخ ذکر شده ای مثل ۲۶ مهر یا ۶ یا ۸ آبان؛ خلاصه که همین روزها اندروید ۴.۴ از راه خواهد رسید. آماده آن هستید؟</div>', '2013-10-17 11:27:37', 1, 'نارنجی', 20),
(38, '6LoWPAN: استانداردی برای اینترنت اشیا', '../newspics/internet of things.jpg', '<p>اگر با ما همراه بوده باشید، حتما نام "<a href="../article-fullpage4.html" target="_blank">اینترنت اشیا</a>" را شنیده اید. ایده ای که بر اساس آن اشیای فیزیکی قادر خواهند بود با اتصال به اینترنت یا به کمک سایر ابزارهای ارتباطی، با اشیای دیگر تعامل داشته باشد. همگام با گسترش این مفهوم، شرکت ها تلاش می کنند راه هایی بیابند که به کمک آن ها بتوان دستگاه ها (یا همان اشیای فیزیکی) را بدون نیاز به ابزارهای واسط، نظیر تلفن های هوشمند یا کامپیوترها، به کمک پروتکل 6LoWPAN به طور مستقیم به سیستم های رایانش ابری متصل کنند.</p>\r\n<div>این ترکیب عجیب و غریب از عدد و حرف، به زبان فارسی یعنی "آی پی نسخه 6 برای شبکه های شخصی کم مصرف". 6LoWPAN در حقیقت نسخه سبک استاندارد آی پی رایج است. در حال حاضر، اکثر دستگاه های بیسیم، از طریق یک دستگاه واسط دیگر مانند تلفن، هاب، یا گِیت وِی، به شبکه بزرگتر متصل می شوند، اما به کمک این پروتکل، اینترنت اشیا به معنی واقعی کلمه ظهور پیدا می کند، که در آن اشیا و سنسور ها مستقیم به وب سرویس متصل خواهند شد.</div>\r\n<div>بنابراین، به جای ارتباط دستگاه با دستگاه، ارتباط مستقیم دستگاه به پردازش ابری، مطرح شده. از آن جایی که در حال حاضر اکثر دستگاه های امروزی برای اتصال به وب از آی پی استفاده می کنند، مهندسان چاره ای ندارند که از همین آی پی معمول برای اشیا و سنسور های کوچک استفاده کنند. اما به این دلیل که این آی پی ها برای استفاده از دستگاه های کوچک و کم مصرف، کمی سنگین هستند، استاندارد دیگری تحت عنوان 6LoWPAN تدوین شد.</div>\r\n<div>عدد 6 در این استاندارد، بیانگر استفاده از آی پی نسخه 6 به جای نسخه قدیمی تر 4 است. استفاده از این نسخه جدیدتر، امکان آدرس دهی به ده ها میلیارد سنسور مختلف را خواهد داد. اما استفاده از آی پی نسخه 6، با توجه به سیستم 128 بیتی آن، برای حافظه و قدرت پردازشی سنسورهای کوچک سنگین به حساب می آید. در نتیجه راه حل این مشکل، تعریف یک نسخه سبک تر از آی پی نسخه 6 خواهد بود.</div>\r\n<div>بنابراین، این نسخه سبک شده پروتکل آی پی، دستگاه های کم مصرف و سنسور های کوچک را قادر می سازد، با دور زدن تلفن هوشمند یا کامپیوتر شخصی، مستقیما به سیستم های ابری متصل شوند. به طور حتم در آینده نزدیک بسیار زیاد نام 6LoWPAN را خواهید شنید.</div>', '2013-10-23 11:48:04', 1, 'نارنجی', 17),
(39, 'گوگل چه برنامه ای برای موتورولا در سر دارد؟', '../newspics/google-motorola.jpg', '<p>حدود دو سال پیش بود که گوگل طی یک خرید ۱۲.۵ میلیارد دلاری، موتورولا را تصاحب کرد.خرید موتورولا توسط گوگل، نوید این را می داد که گوگل رسما وارد دنیای سخت افزار تلفن شده و موتورولا به واحد سخت افزاری گوگل تبدیل خواهد شد. در این میان نزدیک به 6000 تن از کارکنان موتورولا از کار برکنار شدند. با این وجود، در 8 ماه گذشته موتورولا برای گوگل زیان ده بوده و طبق یک برآورد، سالانه حدود یک میلیارد دلار از جیب گوگل خالی می کند.</p>\r\n<p>نقطه جالب ماجرا اینجا است که با همه این گرفتاری ها، گوگل روزهای خوبی را می گذراند. همین چند روز پیش بود که اعلام شد قیمت هر سهم گوگل برای اولین بار از ۱۰۰۰ دلار عبور کرده و آینده درخشانی در انتظار گوگل است. حال سوالی که مطرح می شود این است که اساسا گوگل چرا موتورولا را خرید؟!</p>\r\n<p>در پاسخ به این سوال ابتدا باید به یک سوال دیگر جواب داد: آیا با هزینه هایی که موتورولا برای گوگل تراشیده، گوگل کارش گره خورده و در سراشیبی افتاده؟ جواب این سوال کاملا منفی است. گوگل همچنان بسیار سودده بوده و پرقدرت و استوار در حال رشد است.</p>\r\n<p>گوگل در این چند ماه، موتو اِکس را به بازار معرفی کرده، اولین تلفن هوشمندی که به طور کامل تحت تاثیر گوگل تولید شده. این دستگاه نقدهایی بسیار عالی گرفته، با این وجود هرگز خوب فروش نکرده، و به هیچ وجه نتوانسته ضررهایی که موتورولا متوجه گوگل کرده را جبران نماید. شاید بتوان عمده ترین مانع فروش موتو اِکس را فقدان یک بازاریابی و توزیع درست دانست.</p>\r\n<p>شاید یک دلیل اصلی گوگل برای خرید موتورولا، داستان همیشگی پتنت ها بود. به طوری که گوگل سرمایه گذارانش را متقاعد کرده بود، خرید موتورولا تنها راهی است که از اکوسیستم اندروید، در مقابل رقبا، محافظت خواهد کرد. اما گوگل در این مدت از این استراتژی هم بهره زیادی نبرده.</p>\r\n<p>از پتنت ها و تلفن های هوشمند که بگذریم، یک توجیه دیگر را هم برای خرید موتورولا توسط گوگل می توانیم پیدا کنیم. گوگل در نظر داشت به کمک موتورولا دروازه های نوآوری در زمینه سخت افزار را به روی خود باز کند. به طور مثال ساعت هوشمندی که گوگل در آینده ای نزدیک معرفی خواهد کرد شاید بخشی از همین همکاری موتورولا باشد. ولی در همین زمینه هم تناقض های زیادی دیده می شود. مثلا پروژه کروم کست گوگل توسط موتورولا انجام نگرفت و به یکی دیگر از شرکای گوگل واگذار شد.</p>\r\n<p>احتمالا هنوز خیلی زود است تا بفهمیم گوگل چه برنامه ای در مورد موتورولا در سر دارد. عملکرد کلی گوگل بسیار فوق العاده است، به طوری که گاهی لنگیدن برخی از چرخ های این ماشین غول آسای دنیای دیجیتال، برخی وقت ها، اصلا به چشم هم نمی آید. نظر شما در مورد آینده مشترک گوگل و موتورولا چیست؟</p>', '2013-10-23 11:15:55', 1, 'نارنجی', 19),
(40, 'گوگل برای افزایش طول عمر باتری در دستگاه های اندرویدی شرکت FlexyCore را خرید', '../newspics/google.jpg', '<p>گوگل در تلاش است از هر لحاظ تجربه استفاده از تلفن های هوشمند اندرویدی را بهبود دهد؛ چندی پیش با تعریف پروژه ای با نام Butter، شروع به اصلاح مشکلی کرد که صدای خیلی ها را در آورده بود: عدم روانی عملکرد اندروید.</p>\r\n<div>اما امروز خبر رسید که گوگل این بار برای بهبود طول عمر باتری دستگاه های اندرویدی تا فرانسه سفر کرده و شرکت FlexyCore را به قیمت ۲۳.۱ میلیون دلار خریده. نام این شرکت را پیش از این به عنوان سازنده اپلیکیشن هایی مثل DroidBooster شنیده بودیم که سرعت اجرای اپ ها را بدون کاهش در عمر باتری بالا می برد.</div>\r\n<div>سخنگوی شرکت گوگل در فرانسه گفته که تجارت شرکت فلکسی کور همین حالا هم در تعامل با تیم های اندرویدی است و به علاوه تأیید کرد که این نرم افزار برای بهبود عملکرد دستگاه های اندرویدی به کار خواهد رفت.</div>\r\n<div>این که آیا از توانمندی جدید به همین زودی در اندروید ۴.۴ کیت کت استفاده خواهد شد یا نه دقیق معلوم نیست ولی شاید وعده گوگل مبنی بر عرضه سیستم عامل جدید برای دستگاه های نه چندان قوی، نشانی از چنین چیزی باشد.</div>', '2013-10-23 11:22:16', 1, 'نارنجی', 19),
(41, 'آیفون 5S سریع‌ترین گوشی هوشمند جهان', '../newspics/iphone5s.jpg', '<p>آزمایشات انجام شده روی گوشی‌های هوشمند مختلف توسط نرم افزار محک گیک بنچ حاکی از این است که آیفون 5S سریع‌ترین اسمارت فون موجود در بازار است.<br>به گزارش ایسنا، براساس این آزمایش آیفون 5S رقبای قدرتمندی چون سامسونگ گالکسی S4 را شکست داده و لقب سریع‌ترین گوشی هوشمند جهان را به خود اختصاص داده است.<br>در آزمایش تک هسته‌یی این گوشی‌ها آیفون با اختلاف زیاد ال.جی G2 را شکست داده و در صدر فهرست قرار گرفت و سامسونگ گالکسی S4 ، اچ.تی.سی وان، آیفون 5C در رده‌های سوم تا پنجم قرار گرفتند. همچنین گوشی‌های گالکسی S4 مینی و اچ.تی.سی وان مینی نیز ششم و هفتم شده‌اند.<br>در آزمایش چند هسته‌یی مجددا آیفون 5S در صدر قرار گرفت اما ال.جی G2 با اختلاف کمتری دوم شد. اگرچه آیفون 5S از یک پردازنده دو هسته‌یی بهره می‌برد اما در برابر رقبای چهار هسته‌یی خود بالاتر قرار گرفت و این نشان می‌دهد که تعداد هسته‌ها الزاما به معنای سرعت بالاتر گوشی نیست.<br>این نتایج حاکی از این است که اگر شما به گشت زنی در منوها و نرم افزارهای آیفون 5S بپردازید نسبت به رقبای اندرویدی خود سرعت قابل توجهی را دارد.</p>\r\n<p>نتایج آزمایش نرم افزار محک گیک بنچ:‌<br>آیفون 5S:‌1410 ( امتیاز تک هسته‌یی)، 2561 ( امتیاز چند هسته‌یی)<br>ال.جی G2: 882 (امتیاز تک هسته‌یی)، 2355(امتیاز چند هسته‌یی)<br>سامسونگ گلکسی S4: 687 (امتیاز تک هسته‌یی)، 1939 (امتیاز چند هسته‌یی)<br>اچ.تی.سی وان:‌ 643 (امتیاز تک هسته‌یی)، 1805 (امتیاز چند هسته‌یی)<br>آیفون 5C: 711 (امتیاز تک هسته‌یی)، 1281 (امتیاز چند هسته‌یی)<br>سامسونگ گلکسی S4 مینی: 649 (امتیاز تک هسته‌یی)، 1135 (امتیاز چند هسته‌یی)<br>اچ.تی.سی وان مینی: 477 (امتیاز تک هسته‌یی)، 880 (امتیاز چند هسته‌یی) </p>', '2013-10-26 13:01:17', 2, 'سايت خبري عصر ايران', 17),
(42, 'ساخت اولین لامپ جهان که وای‌فای منتشر می‌کند', '../newspics/wifi-lamp.jpg', '<p>گروهی از دانشمندان چینی توانستند اولین لامپ برقی جهان را ابداع کنند که سیگنال وای‌فای از خود منتشر می‌کند.<br>به گزارش ایسنا، محققان دانشگاه فودان شانگهای به تولید پیش‌ساختی برای این فناوری پرداخته و آنرا «لی-فای» نامیده‌اند.<br>به ادعای کارشناسان، این لامپ ال‌ای‌دی حاوی ریز تراشه از عملکرد بهتری نسبت به میانگین ارتباطات اینترنتی بی‌سیم در چین برخوردار است.<br>تا چهار دستگاه در نزدیکی این لامپ می‌توانند با استفاده از فرکانسهای نور بجای امواج رادیویی به سیگنال آن متصل شوند.<br>در این لامپ تراشه‌ای قرار گرفته که سیگنال وای‌فای تولید می‌کند و با سرعت 150 مگابایت بر ثانیه، بطور چشمگیری از سرعت بیشتری نسبت به میانگین ارتباطات وای‌فای در این کشور برخوردار است.<br>ماه آینده محققان به رونمایی از 10 لامپ نمونه و بسته‌های همراه آنها در نمایشگاه بین‌المللی صنعت در شانگهای خواهند پرداخت.<br>به گفته کارشناسان، این روش سریعتر، ارزانتر و کارآمدتر ارتباط به اینترنت می‌تواند در خانه‌های چینی‌ها که رو به استفاده بیشتر از چراغهای ال‌ای‌دی بجای لامپهای سنتی آورده‌اند، رواج بیشتری پیدا کند.<br>طبق گزارشات، فناوری لی-فای تنها از پنج درصد انرژی مورد نیاز برای تامین نیروی سایر دستگاههای وای‌فای که بر سیستمهای خنک‌کننده برای تامین اینترنت برجها و ایستگاه‌های تلفن همراه تکیه دارند، استفاده می‌کند.<br>اگرچه این فناوری جدیدی با گرایشات فناورانه در حال تغییر چین از تناسب زیادی برخوردار است اما سازندگان با مشکلاتی در طراحی و ساخت آن مواجه شده‌اند که چالش اصلی آن، طراحی ریزتراشه این دستگاه است.<br>همچنین ارتباط اینترنتی در صورت خاموش شدن چراغ یا رفتن برق قطع خواهد شد. </p>', '2013-10-26 13:04:30', 2, 'سايت خبري عصر ايران', 17),
(43, 'گوگل ساعت هوشمند می‌سازد', '../newspics/smartclock.jpg', '<p>شرکت گوگل برای اینکه از شرکت‌های سونی و سامسونگ در عرصه ساخت ساعت‌های هوشمند عقب نماند، نکسوس‌واچ را عرضه خواهد کرد.<br>این اسمارت واچ گوگل در مرحله نهایی تولید قرار دارد و شایعات حاکی از اینست که گوگل قابلیت‌های عینک خود را در این ساعت نیز گنجانده است.<br>سرویس جدید "اکنون گوگل" (google now) که امکان سوال و جواب در لحظه را فراهم می‌کند، روی این ساعت قرار داده خواهد شد و کاربران می‌توانند با استفاده از این قابلیت سوال صوتی خود را مطرح کرده و منتظر پاسخ بمانند.<br> احتمال می‌رود که نکسوس واچ گوگل در تاریخ 31 اکتبر رونمایی شود. </p>', '2013-10-26 13:10:28', 2, 'سايت خبري عصر ايران', 17),
(44, 'گوگل از شما براي تبليغ استفاده مي كند', '../newspics/google.jpg', '<p>تغییرات جدید در مقررات خدمات گوگل نشان می‌دهد این شرکت قصد دارد از اسامی و تصاویر کاربرانش در تبلیغات محصولاتی که مورد تایید آنها است استفاده کند.<br>براساس گزارش فاکس نیوز، در مقررات جدید گوگل تبلیغات را  "تایید به اشتراک‌گذاشته شده" نامیده و قصد دارد با استفاده از آن تبلیغات پنهانی انجام دهد.<br>به این شکل درصورتی که کاربری در یک صفحه اظهار نظر کند، صفحه‌ای را دنبال کند و یا در گوگل پلاس به آن امتیاز بدهد، گوگل از این فعالیت‌ها برای ارتقا آنچه از محصول گرفته تا شرکت یا وب‌سایت، مورد تایید کاربر است استفاده می‌کند و در این راه از تصویر و نام  کاربر نیز در تبلیغ مورد تایید‌ها استفاده خواهد کرد. ظاهرا گوگل تلاش دارد تا این خدمات را در قالب یک ابزار کاربردی نشان دهد اما در حقیقت می‌خواهد از محتوای اطلاعات کاربری برای تبلیغات ارزان‌قیمت استفاده کند.<br>با این‌همه گوگل در مقررات جدید خود به کاربر اجازه داده تا کنترل اطلاعاتی که دیگران از او می‌بینند را در دست داشته‌باشد،‌ برای مثال اگر فردی در گوگل پلاس فعالیت داشته باشد و فعالیت‌های کاربر در گوگل پلاس تنها برای گروهی مشخص از افراد محدوده شده باشد، تنها همان گروه می‌توانند تایید به اشتراک‌گذاشته شده را ببیند. این محدودیت درمورد مطالب اسپانسردار فیس‌بوک نیز رعایت می‌شد.<br>مطالب اسپانسردار در سال 2011 توسط فیس‌بوک اجرا شد که مورد انتقاد شدید کاربران قرار گرفت. مطالب اسپانسردار صفحه‌هایی که کاربران فیس‌بوک آنها را تایید می‌کردند و همچنین پست‌هایی که حاوی نام یکی از شرکت‌ها و محصولات تجاری بودند را به تبلیغات تبدیل می‌کرد. برای مثال اگر کاربری در پست خود به مارک استارباکس اشاره می‌کرد، فیس‌بوک آن را به مطلبی اسپانسردار برای تبلیغ استارباکس تبدیل می‌کرد اما تنها آن را در دسترس افرادی قرار می‌داد که در گروه دوستان کاربر قرار داشتند.<br>تفاوت تایید‌های به اشتراک‌گذاشته شده گوگل و مطالب اسپانسردار فیس‌بوک در این است که کاربران فیس‌بوک نمی‌توانستند استفاده از این گزینه را انتخاب کنند اما کاربران گوگل می‌توانند. گوگل اعلام کرده کاربرانی که دوست ندارند اطلاعاتشان در این برنامه مورد استفاده قرار بگیرد باید به بخش تنظیمات تایید‌های به اشتراک‌گذاشته شده رفته و آن را خاموش کنند، به این شکل تصویر و نام کاربر در تبلیغات محصولات مورد علاقه‌شان مورد استفاده قرار نخواهد گرفت.<br>گوگل با افزودن این گزینه به تنظیمات ابزار جدیدش راه فراری را برای خود ایجاد کرده تا به سرنوشت فیس‌بوک دچار نشود، زیرا فیس‌بوک مجبور به متوقف کردن برنامه تبلیغاتی خود شده و هزینه‌ای 20 میلیون دلاری برای حل و فصل شکایتی که علیه این برنامه شده بود را متقبل شد. </p>', '2013-10-27 12:20:51', 2, 'سايت خبري عصر ايران', 18),
(45, 'اپلیکیشن جعلی iMessage از گوگل پلی حذف شد', '../newspics/imessage.jpg', '<p> اپلیکیشنی که اخیرا با نام iMessage Chat به فروشگاه گوگل پلی اضافه شده بود، به واسطه نگرانی های امنیتی و نقض قوانین این فروشگاه، زیاد دوام نیاورد و حذف شد.<br>این اپلیکیشن که سازنده اش شرمی از بیان شباهت های آن به iMessage اپل نداشت، از عناصر بصری و زبان طراحی iOS 7 تقلید کرده بود و البته آنطور که برخی تصور می کنند دنیای اندروید را به آی او اس متصل نمی کرد.<br>در اصل این اپلیکیشن تنها پیام ها را با پروتکل iMessage بین دستگاه های اندرویدی جا به جا می کرد و این کار را با حقه های دیجیتال و جا زدن فرستنده پیام به عنوان یک مک مینی انجام می داد.<br>حالا خوشبختانه این اپلیکیشن که امنیت آن نیز زیر سوال بود از گوگل پلی حذف شده و کاربران با فریب خوردن از نام آن، اطلاعات حساس شان را در دسترس توسعه دهنده اش قرار نمی دهند.</p>', '2013-10-27 12:30:21', 2, 'نارنجي', 20);
INSERT INTO `news` (`id`, `subject`, `image`, `body`, `ndate`, `userid`, `resource`, `catid`) VALUES
(46, 'امکان خرید گوگل گلس از طریق دعوتنامه فراهم می شود', '../newspics/googleglass.jpg', '<p> گوگل گلس گجتی محبوب است که خیلی ها آرزوی خریدنش را دارند. اما این شرکت هنوز فروش عمومی آن را آغاز نکرده و این گجت تنها در اختیار عده محدودی قرار گرفته تا ضمن تست آن به عیب یابی و توسعه اش کمک کنند.<br>حالا گوگل قدم تازه ای را برای فروش گوگل گلس برداشته است و به دارندگان فعلی این محصول اجازه می دهد تا ۳ نفر از دوستانشان را برای خرید گوگل گلس دعوت کنند.<br>می دانیم که در حال حاضر حدود ۱۰ هزار نفر از گوگل گلس استفاده می کنند و دعوتنامه جدید به این معنی است که گوگل قصد دارد حدود ۳۰ هزار کاربر جدید را به برنامه آزمایشی گوگل گلس وارد کند.<br>در عین حال گوگل اعلام کرده دارندگان فعلی گلس می توانند برای یک بار عینک خودشان را با یک عینک جدید جایگزین نمایند.<br>البته شرایط برای خرید گوگل گلس همچنان سخت گیرانه است و برای دریافت دعوتنامه و خرید گلس لازم است که در کشور آمریکا زندگی کنید و محل تحویل این گجت نیز فقط در آمریکا خواهد بود.<br>اما به نظر می رسد که این عینک دوست داشتنی یک گام دیگر به بازار مصرفی عمومی نزدیک شده و احتمالا فاصله زیادی با فروش عمومی آن نخواهیم داشت.</p>', '2013-10-29 10:44:29', 2, 'نارنجي', 18),
(47, 'سوءاستفاده جدید هکرها از حفره جاوا', '../newspics/java.jpg', '<p>هکرهای اینترنتی سوءاستفاده از یکی از حفره‌های برنامه جاوا برای طراحی بدافزار و حمله به کاربران وب‌سایت‌های طراحی شده بر مبنای php.net را آغاز کرده‌اند.<br>به گزارش فارس به نقل از رجیستر، برخی کاربران وقتی متوجه این موضوع شدند که در زمان استفاده از خدمات مرورگر ایمن شرکت گوگل با پیام های هشداردهنده‌ای در مورد آلودگی برخی وب سایت های مورد بازدید مواجه شدند. بررسی موضوع نشان داد که بدافزار خطرناکی بر مبنای آسیب پذیری فوق طراحی شده است.<br>بدافزار یاد شده رایانه های کاربرانی را که به وب سایت‌های آلوده شده مراجعه کنند، دچار مشکل می کند و هنوز حفره ای در برنامه جاوا که عامل این مشکل بوده، برطرف نشده است. هنوز مشخص نیست چه افرادی عامل طراحی این بدافزار هستند.<br>با توجه به اینکه امکان سرقت کلمات عبور کاربران از این طریق وجود دارد، به آنها توصیه شده تا از کلمات عبور جدید استفاده کرده و در صورت امکان از پروتکل ایمن https برای تبادل داده در فضای مجازی استفاده نمایند. پیش از این نیز بارها حملاتی با سوءاستفاده از ضعف های برنامه جاوا برای آلوده کردن رایانه های کاربران طراحی شده بود.</p>', '2013-10-29 10:49:02', 2, 'سايت خبري عصر ايران', 18),
(48, 'به زودی تلویزیون ها و یخچال های سامسونگ با هم صحبت خواهند کرد', '../newspics/samsung.jpg', '<p>بالاخره سامسونگ اولین قدم را در جهت تحقق اینترنت اشیا برداشت، و SDK تلویزیون هوشمند جدید خود را در کنفرانسی که در سانفرانسیسکو برگزار کرد، در اختیار توسعه دهندگان قرار داد.<br>تلویزیون های هوشمند جدید سامسونگ قادر خواهند بود، آخرین وضعیت یخچال، ماشین لباسشویی و کولر را مستقیما در نمایشگر خود نشان دهند. به طور مثال به ما بگویند که ماشین لباسشویی در فرایند شستن لباس ها در چه مرحله ای است، یا این که آیا کسی در یخچال را باز کرده یا خیر. حتی به آسانی می توان جهت باد کولر را، در هنگام تماشای فیلم، از طریق تلویزیون تغییر داد. احتمالا در گام های بعدی این تلویزیون ها قادر خواهند بود با چراغ های خانه و یا محصولات خانگی سایر تولید کنندگان هم ارتباط برقرار کنند.<br>طرفداران اینترنت اشیا مدت ها منتظر عملی شدن چنین ارتباطاتی میان وسایل پرکاربرد خانه بودند، ولی به ندرت پیش می آمد که یک شرکت بزرگ و یا یک تولید کننده عمده به وضوح در این مسیر گام بردارد. از طرف دیگر، اسمارت تی وی ها یا همان تلویزیون هایی که با پسوند هوشمند به بازار عرضه می شدند، همه چیزی در آن ها یافت می شد، جز هوش. اما این بار گویا سامسونگ به طور جدی وارد این عرصه شده، و در نظر دارد بار دیگر نوآوری و پیشتاز بودن خود را به رخ رقبا بکشد و کم کم یک سبک زندگی جدید را نیز به تصویر در آورد.<br>سامسونگ در بازار تلویزیون ها دست بالا را دارد، به طوری که گفته می شود این شرکت سال گذشته 53 میلیون تلویزیون فروخته. در حالی که تماشای آنلاین ویدیو از طریق تلویزیون ها هنوز جا نیافتاده، با این حال نزدیک به 20% صاحبان تلویزیون های هوشمند سامسونگ در ایالات متحده آمریکا از این قابلیت تلویزیون ها استفاده می کنند. بنابراین همه چیز برای سامسونگ آماده است تا در این بازار جدید وسایل خانگی متصل به هم، از رقبا پیشی بگیرد.</p>', '2013-10-30 11:36:10', 2, 'سايت خبري عصر ايران', 17),
(49, 'خدمات کلود مایکروسافت متنوع‌تر می‌شود', '../newspics/microsoft.jpg', '<p>مایکروسافت قصد دارد با گنجاندن خدمات کلود در برخی محصولات خود مانند ویژوال بیسیک و .NET بر میزان علاقه کاربران به استفاده از آنها بیفزاید.<br>به گزارش فارس به نقل از آی دونیوز، با توجه به ارائه گسترده خدمات کلود از سوی بسیاری از شرکت‌های بزرگ فناوری مایکروسافت نیز برای عقب نماندن از آنها دسترسی به خدمات کلود را در برخی محصولات خود ممکن کرده است.<br>از جمله این برنامه ها و محصولات که به روز شده وبا قابلیت های کلود عرضه شده اند می توان به Visual Basic  و .NET ، نسخه های به روز شده ویندوز سرور، System Center  و  Azureو ... اشاره کرد. در این میان امکاناتی که به سیستم عامل های Windows Server 2012 R2  و  System Center 2012 R2 افزوده شده بسیار چشمگیر توصیف شده است.<br>هزینه اندک دسترسی به خدمات کلود و تقویت زیرساخت‌ها از جمله برنامه‌های مایکروسافت برای جلب نظر کاربران است. از این پس همچنین می توان با استفاده از Visual Studio 2013 و آخرین نسخه به روز شده .NET یعنی .NET 4.5.1 برنامه ها و خدمات متنوعی را بر مبنای پلاتفورم های کلود طراحی نمود.<br>مایکروسافت همچنین قصد دارد با همکاری شرکت Equinix Inc. امنیت خدمات کلود خود را در زمان اتصال به شبکه‌های گوناگون افزایش دهد.</p>', '2013-11-03 12:55:46', 2, 'سايت خبري عصر ايران', 18),
(50, 'شكست و حذف iGoogle از خدمات گوگل ', '../newspics/google.jpg', '<p>سرویس شخصی‌سازی خدمات گوگل موسوم به iGoogle به علت عدم استقبال کاربران به زودی به تاریخ می‌پیوندد.<br>به گزارش فارس به نقل از رید اند رایت، علیرغم اعتراضات بسیاری از کاربران خدمات مذکور، قرار است سرویس iGoogle از امروز به طور کامل از گوگل محو شود. کارشناسان می گویند با توجه به محبوبیت گسترده شبکه‌های اجتماعی و خدمات مشابه دیگر iGoogle هیچ شانسی برای موفقیت نخواهد داشت.<br>صفحه اختصاصی این خدمات به نشانی google.com/ig نیز حذف شده و کاربران به جای ورود به آن به صفحه اول گوگل هدایت می‌شوند.<br>برنامه ریزی برای حذف iGoogle از شانزده ماه قبل آغاز شده بود، به خصوص که گوگل خود با راه اندازی سرویس های متعدد دیگری و افزودن قابلیت های شخصی سازی به آنها از اهمیت iGoogle کاسته بود. خدمات شخصی سازی مرورگر و سیستم عامل کروم، سرویس اجتماعی گوگل پلاس و همین طور پلاتفورم آندروید از جمله این موارد محسوب می شود.<br>برای طراحی iGoogle از برنامه آژاکس استفاده شده بود تا کاربران بتوانند خدمات مختلف گوگل که به آنها علاقه دارند را انتخاب کرده و به هر ترتیبی که دوست دارند در صفحه شخصی شده خود به نمایش بگذارند.</p>', '2013-11-05 11:43:53', 2, 'سايت خبري عصر ايران', 18),
(51, '۴۰۰ میلیون دلار هزینه برای بازاریابی ویندوز', '../newspics/microsoft.jpg', '<p>مایکروسافت قصد دارد برای بازاریابی سیستم عامل ویندوز خود در فصل تعطیلات کریسمس رقم بی‌سابقه ۴۰۰ میلیون دلار هزینه کند.<br>به گزارش فارس به نقل از تک اسپات، این اقدام در حالی صورت می‌گیرد که این شرکت در کریسمس سال گذشته میلادی تنها 241 میلیون دلار را به این منظور هزینه کرده بود و در شرایط دشوار اقتصادی فعلی قصد دارد این هزینه را دو برابر کند.<br>از این رقم 131 میلیون دلار صرف تبلیغات برای مشتریان حقیقی و 274 میلیون دلار صرف هزینه برای بازاریابی به روش سنتی و دیگر مخارج عملیاتی خواهد شد.<br>در حالی که برخی کارشناسان صرف چنین هزینه‌هایی را زیاده‌روی می دانند، عده ای دیگر معتقدند با توجه به اهداف از پیش تعیین شده مایکروسافت بودجه اختصاص یافته منطقی به نظر می‌رسد. به خصوص که رقبای این شرکت برای از میدان به در کردن ویندوز به عنوان یک سیستم عامل سنتی تلاش های فراوانی را به عمل آورده اند.<br>مایکروسافت امیدوار است تا قبل از پایان سال 2013 بتواند 16 میلیون تبلت مجهز به سیستم عامل ویندوز به فروش برساند و به همین منظور مذاکراتی را با شرکت های دل، ایسر و لنووو هم به عمل آورده است.</p>', '2013-11-05 11:45:32', 2, 'سايت خبري عصر ايران', 20),
(52, 'استفاده از هلیوم برای تولید هارد دیسک های 6 ترابایتی', '../newspics/hard.jpg', '<p>از این پس علاوه بر سرد سازی آهنرباهای مغناطیسی ذره کوب بزرگ سرن، هلیوم کاربرد آشنا و عیجب دیگری هم پیدا کرده است: افزایش ظرفیت، کارایی و راندمان هارد دیسک ها.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">وسترن دیجیتال امروز اعلام کرد که HGST (یکی از شرکت های تابعه) فروش هارد دیسک های ۶ ترابایتی Ultrastar He6 را آغاز کرده است. در این مدل از هارد دیسک، پلاترهای گردان آن درون یک جایگاه سحر آمیز پر شده از هلیوم (به جای هوا) قرار گرفته اند.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">به دلیل اینکه چگالی هلیوم یک هفتم هوا است، تلاطم به وجود آمده در اثر کار مداوم بخش های گردان دیسک و هد هایی که مرتبا روی آنها برای خواندن اطلاعات در حرکتند، کاهش خواهد یافت.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">و البته این به معنی مصرف کمتر انرژی و اتلاف کمتر گرما هم خواهد بود که در دیتاسنترها امری مهم و حیاتی محسوب می شود. به خصوص اینکه وسترن دیجیتال ادعا می کند با استفاده از هلیوم، میزان مصرف انرژی به ازای هر ترابایت تا ۴۹ درصد کاهش می یابد.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">با کاهش اغتشاش و تلاطم در کار درایو، امکان افزایش ظرفیت آن هم محیا می شود. زیرا با این شیوه می توان پلاترهای بیشتری را در اتاقک ۳.۵ اینچی هارد جای داد. هاردهای ۴ ترابایتی کنونی از ۵ پلاتر بهره می برند. اما درون Ultrastar He6، هفت پلاتر قرار گرفته و ظرفیت را به ۶ ترابایت رسانده است.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">البته در ماه های اخیر، هارد درایوها تحت فشار رقابتی شدیدی از سوی حافظه های فلش قرار داشته اند که سریعتر و بی صداتر هستند. اما برگ برنده هارد دیسک ها تا کنون هزینه کمتر به ازای هر گیگابایت بوده است.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">ایده هاردهای بر پایه هلیوم می تواند اساس طراحی های جدید وسترن دیجیتال باشد، تا با افزایش حجم و بهبود کارایی هارد دیسک ها مزیت رقابتی آنها را بالاتر ببرد. به گفته وسترن دیجیتال، هارد دیسک های Ultrastar He6 نسبت به درایوهای ۴ ترابایتی فعلی، ۲۳ درصد مصرف انرژی کمتری دارند.</p>', '2013-11-05 11:55:43', 2, 'نارنجي', 17),
(53, 'سیستم عامل جدید اپل حضور کاربر را تشخیص می‌دهد', '../newspics/apple.jpg', '<p>یک برنامه‌نویس قابلیت جالبی را در نسخه جدید سیستم عامل مک شرکت اپل موسوم به ماوریکس شناسایی کرده که تشخیص حضور کاربر در برابر رایانه را ممکن کرده است.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">به گزارش فارس به نقل از اپل اینسایدر، حسگر حساس به نور رایانه های مک بوک پرو با استفاده از برنامه ای که در سیستم عامل ماوریکس نصب شده، می تواند حرکات کاربر را در برابر رایانه تشخیص دهد و در صورتی که عدم حضور وی در برابر رایانه طولانی شود، ابتدا نمایشگر خاموش شده و سپس هارددیسک نیز خاموش می گردد و رایانه در وضعیت sleep قرار می گیرد.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">پیش از این دوربین iSight رایانه های مک برای اقدامات عادی دیگری مورد استفاده قرار می گرفتند، اما این اولین بار است که چنین قابلیتی با استفاده از سیستم عامل های اپل بر روی رایانه های مذکور فعال شده است. جالب آنکه با تغییر وضعیت نور محیط قرارگیری رایانه، عملکرد سیستم هم بر مبنای آن متحول می گردد.<br style="color: #000000; font-family: Tahoma, Arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff;">گفتنی است حسگرهای نور محیطی در تمامی مدل های مک بوک پرو و همین طور مدل های اخیر رایانه های آی مک و نمایشگرهای پیشرفته و جدید این شرکت وجود دارد و لذا قابلیت یاد شده برای اکثر کاربران سیستم عامل مک قابل استفاده است. سیستم عامل ماوریکس که حدود 10 روز قبل عرضه شد، مورد استقبال گسترده کاربران قرار گرفته است.</p>', '2013-11-05 11:58:12', 2, 'سايت خبري عصر ايران', 20),
(54, 'گوگل با کیت‌کت به جنگ آفیس مایکروسافت می‌رود', '../newspics/AndroidKitKat.jpg', '<p>گوگل برای کاهش محبوبیت نرم افزار آفیس که پس از ویندوز پرطرفدارترین نرم افزار تولیدی مایکروسافت است، برنامه‌های جدیدی در سر دارد.<br>به گزارش فارس به نقل از کامپیوترورلد، قرار است در تازه‌ترین نسخه از سیستم عامل آندروید گوگل یعنی نسخه 4.4 که به کیت‌کت هم معروف شده، برنامه Quickoffice به طور پیش فرض گنجانده شود. این برنامه دارای بسیاری از امکانات برنامه آفیس است.<br>گوگل امیدوار است Quickoffice با امکانات خاصی که دارد ابتدا در محیط های تجاری مورد توجه قرار بگیرد و سپس کاربران خانگی هم از آن استقبال کنند. این اولین بار است که گوگل نرم افزاری با اهمیت و ابعاد Quickoffice را به طور پیش فرض در سیستم عامل خود می گنجاند.<br>مایکروسافت تاکنون برنامه‌های محدودی مانند مرورگر و نرم افزار player خود را به طور پیش فرض در ویندوز گنجانده بود، اما این امر در مورد آفیس مصداق نداشته، زیرا مایکروسافت از فروش آن سود خوبی کسب می کند.<br>گوگل شرکت سازنده Quickoffice را در سال 2012 خریداری کرد و از آن زمان در حال ارتقای این برنامه برای افزایش تعداد کاربران آن است. این برنامه دارای نسخه‌های رایگان و پولی برای کاربران تجاری است.</p>', '2013-11-07 12:54:28', 2, 'سايت خبري عصر ايران', 20),
(55, 'آسیب‌ پذیری ویندوز ايكس پي (XP) در برابر ویروس‌ها پنج برابر ویندوز ۸', '../newspics/win.jpg', '<p>نتایج یک بررسی تازه نشان می‌دهد میزان آسیب‌پذیری ویندوز XP در برابر حملات بدافزارها و ویروس‌های رایانه‌ای پنج برابر بیشتر از ویندوز ۸ است.<br>به گزارش فارس به نقل از اسلش گیر، گزارشی که توسط خود مایکروسافت تهیه شده نشان می‌دهد که از هر هزار رایانه اسکن شده، 9.1 مورد از رایانه های مجهز به ویندوز XP که بر روی آنها سرویس پک 3 نیز نصب شده بود، آلوده به ویروس بودند. در مقابل تنها 1.6 رایانه‌های مجهز به ویندوز 8 ویروسی بوده و باید پاکسازی می‌شدند.<br>گزارش مذکور ادعاهای قبلی مایکروسافت در مورد آسیب پذیری بیشتر سیستم عامل های قدیمی این شرکت را تایید می‌کند. این شرکت بارها از کاربران ویندوزهای قدیمی تر خود مانند ویستا، 7 و XP خواسته تا هر چه سریع تر به ویندوزهای جدید و از جمله ویندوزهای 8 یا 8.1 مهاجرت کنند.<br>البته میزان مصونیت ویندوز 8.1 در این گزارش بررسی نشده، اما مایکروسافت مدعی است امنیت ویندوز 8.1 حتی از میزان امنیت ویندوز 8 هم بالاتر است.<br>از جمله امکانات جدید امنیتی در ویندوز 8.1 می‌توان به امکان پاکسازی اطلاعات از راه دور برای صاحبان مشاغل، پشتیبانی بیومتریک ارتقا یافته، کدگذاری پیشرفته داده ها، افزایش امنیت مرورگر IE، مقاومت بهتر در برابر بدافزارها و ... اشاره کرد.</p>', '2013-11-07 13:00:26', 2, 'سايت خبري عصر ايران', 20),
(56, 'رونمایی موتورولا از Moto G در ۲۲ آبان', '../newspics/motorola.jpg', '<p>در روزهای گذشته شاهد آن بودیم که تلفن جدید موتورولا با نام Moto G  چند باری لو رفت و حالا این شرکت خجالتی بودن را کنار گذاشته و سایتی با نام moto-g برای این محصول جدید راه اندازی کرده که وعده رونمایی در روز ۲۲ آبان را می دهد.<br>در این سایت اطلاعاتی از محصول به چشم نمی خورد و البته نیازی هم به این منبع برای خبر گرفتن نداریم، چرا که پیش از این نمایشگر ۴.۵ اینچی 720p، پردازنده اسنپدرگون S4 pro چهار هسته ای، دوربین ۵ مگاپیکسلی، و اندروید ۴.۳ برای آن لو رفته اند.<br>سر انجام به نظر می رسد موتورولا به عملی کردن قولی که فردای رونمایی از موتو ایکس داد، نزدیک می شود؛ عرضه تلفنی ارزان قیمت برای بازار جهانی.</p>', '2013-11-07 13:03:51', 2, 'نارنجي', 17),
(57, 'رونمایی از نسل جدید عینک هوشمند گوگل', '../newspics/googleglass2.jpg', '<p>نسل جدید عینک هوشمند گوگل با تغییرات کوچکی در طراحی رونمایی شد.<br> به نقل از اینگجت گوگل امکان جایگزینی عینک هوشمند خود را با نسخه جدید فراهم کرده و از نسل دوم این محصول با تغییری کوچک در طراحی رونمایی کرده است.<br>در نسل دوم عینک‌های هوشمند گوگل دیگر خبری از سیستم پر نقص القای استخوانی برای انتقال صدا به گوش درونی کاربران نیست و همین گوش بیرونی کفایت می‌کند.<br>در عینک هوشمند جدید گوگل  یک هدفون در سمت راست دستگاه با استفاده از پورت میکرو USB به عینک متصل شده و خیلی راحت داخل گوش کاربر قرار می گیرد تا وظیفه انتقال صدا را ایفا کند.</p>', '2013-11-07 13:06:26', 2, 'سايت خبري عصر ايران', 17),
(58, 'آیا سونی نسخه Wi-fi only اکسپریا زد اولترا را عرضه می کند؟', '../newspics/sony.jpg', '<p>احتمالا در شرف یک اتفاق جالب هستیم. بر طبق اسناد ثبت محصول در امریکا، تبلت غول پیکر اکسپریا زد اولترا دارد صاحب یک برادر خوانده از نوع Wifi only می شود، یعنی در اصل تبدیل به یک مینی تبلت بدون امکانات مخابراتی و مکالماتی.</p>\r\n<div>در اسناد ثبتی، از این محصول که با نام TS-0010 به کمیسیون ارتباطات فدرال امریکا معرفی شده، هم به عنوان یک هندست قابل حمل و هم مینی تبلت یاد شده ولی هیچ آنتن سلولاری در آن نیست و در عوض از وای فای 802.11 a/b/g/n/ac، بلوتوث ۴.۰ کم مصرف، ان اف سی و جی پی اس بهره می برد.</div>\r\n<div>بهترین پیش بینی این است که سونی بالاخره رضایت داده تا اکسپریا زد اولترا نقش واقعی اش را در بازار بر عهده بگیرد و رسما یک مینی تبلت شود. اتفاقی که بد نبود از همان اول هم به عنوان یک انتخاب در نظر گرفته می شد.</div>', '2013-11-07 13:17:56', 2, 'نارنجي', 17),
(59, 'به زودی اندروید در تماس های دریافتی از تصویر پروفایل گوگل پلاس افراد استفاده خواهد کرد', '../newspics/google+.jpg', '<p>همانطور که تا کنون متوجه شده اید، اپلیکیشن تلفن مربوط به اندرويد 4.4، ظاهری جدید پیدا کرده و مشخصه های مختلفی به آن اضافه شده. خبرها حاکی از این است که بخش تماس های اندروید هم تا اوایل سال 2014 به دیتابیس عظیمی از تصاویر مخاطبین مجهز می گردد. این دیتابیس چیزی نیست جز همان تصاویر پروفایل اشخاص در گوگل پلاس. به طوری که شماره تلفن تائید شده افراد در حساب های گوگل، با تصویر پروفایل گوگل پلاس آن ها جفت می شود.</p>\r\n<div>در این صورت هر وقت شما با فردی که تلفن اندرویدی دارد تماس بگیرید، تصویر پروفایل گوگل پلاس شما بر روی صفحه تماس های دریافتی او نقش می بندد، و صد البته این که هر گاه آن شخص با شما تماس بگیرد، شما هم تصویر پروفایل گوگل پلاس او را خواهید دید. این قابلیت، به ویژه، زمانی سودمند خواهد بود که شما شماره آن شخص را به لیست مخاطبین خود اضافه نکرده باشید، ولی چهره آن شخص را از روی تصویرش تشخیص دهید.</div>\r\n<div>این مشخصه جدید در اندروید به شما این امکان را می دهد که به جای این که دیگران برای شماره شما یک تصویر انتخاب کنند، خودتان تصویر مرتبط با شماره تان را در دستگاه های آن ها تعیین کنید. البته هنوز مشخص نیست که دقیقاً کدام یک از نسخه های اندروید از این ویژگی بهره خواهند برد. با این حال گوگل باز هم نشان داد که قصد دارد از حجم بسیار گسترده داده های کاربرانش بهترین استفاده را کرده، و با عرضه قابلیت هایی متکی به این داده های منحصر به فرد، و یکپارچه کردن خدمات متنوعش، سایر رقبا به چالش بکشاند</div>', '2013-11-07 13:22:33', 2, 'نارنجي', 18),
(60, 'Titan Arm، بازویی رباتیک که به افراد قدرتی فرا انسانی می دهد', '../newspics/titanarm.jpg', '<p>در طول تاریخ، تبدیل شدن به یک ابر انسان با توان فیزیکی بسیار بالا، همواره از یکی آرزوهای بشری بوده . این حقیقت را می توان به آسانی در افسانه ها، کمیک استریپ ها و فیلم ها مشاهده کرد. گویا دانشجویان دانشگاه پنسیلوانیا قدمی هر چند کوچک ولی مهم در این رابطه برداشته اند. آن ها بازویی رباتیک خلق کرده اند که توان بازوی انسان را به میزان زیادی افزایش می دهد.</p>\r\n<div>این بازوی رباتیک موسوم به تایتان آرم (Titan Arm)، تا ۲۰ کیلوگرم بر توان بلند کردن اشیا توسط افراد می افزاید. به طور مثال اگر شما به آسانی جسمی با وزن 10 کیلوگرم را بلند می کنید، توسط این بازو به آسانی از عهده اجسامی با وزن ۳۰ کیلوگرم برخواهید آمد. مخترعین این وسیله موفق به کسب جایزه ۴۵ هزار دلاری جیمز دایسون اَوارد (James Dyson Award) شده اند.آن چه بازوی تایتان را از محصولات مشابه متمایز می کند، تکنیک های مدرن و ارزان تر به کار رفته در ساخت آن است. در این اسکلت خارجی رباتیک، از موتورهای کوچک و فشرده پَنکِیکی بهره گرفته شده. همچنین استفاده از آلمینیوم، تا حدی هزینه های مربوط به ساخت را کاهش داده.مخترعین جوان تایتان آرم در گام بعدی قصد دارند جنبه تجاری اختراع شان را تقویت کنند. آنها اعتقاد دارند به زودی این محصول بازار خود را برای بیماران جسمی حرکتی و کارگران یدی، پیدا خواهد کرد. لازم به ذکر است که تایتان آرم، انرژی خود را از طریق باتری تامین می کند.</div>\r\n<div>گرچه این اختراع نسبت به محصولات مشابه، قیمت تمام شده کمتری خواهد داشت، با این حال، با هزینه تولید ۱۹۰۰ دلاری، شاید تایتان آرم هنوز آمادگی تجاری شدن را نداشته باشد. مخترعین تایتان آرم قصد دارند، جایزه ۴۵ هزار دلاری شان را صرف توسعه این محصول و حفظ پتنت های مربوط به طراحی آن کنند.</div>', '2013-11-11 23:45:01', 2, 'نارنجی', 17),
(61, 'یکی از مدیران سابق اپل هم اکنون در سامسونگ بر روی یک پروژه مرتبط با اینترنت اشیا کار می کند', '../newspics/internetthings.jpg', '<p>پیش از این نوشتیم که سامسونگ از مسیر تلویزیون های هوشمند  قدم هایی جدی برای ورود به عرصه اینترنت اشیا برداشته . حال خبر رسیده که لوک جولیا، مهندس نرم افزار، و مدیر سابق تیم Siri در شرکت اپل، هم اکنون در سامسونگ بر روی پلتفرمی مرتبط با اینترنت اشیا کار می کند.</p>\r\n<div>لوک جولیا در سال 2012 اپل را به مقصد سامسونگ ترک کرد و در حال حاضر نفر دوم مرکز نوآوری های باز سامسونگ است. او در این شرکت بر روی پروژه "معماری سامسونگ برای تعاملات چند حالته" موسوم به سامی (SAMI) کار می کند. این پلتفرم، داده های دستگاه ها و اشیای متصل به شبکه را جمع آوری، پردازش و توزیع می نماید. پلتفرم سامی، قادر خواهد بود تمام داده های مربوط به گجت های پوشیدنی، خانه های هوشمند و حتی خودروها را در یک اپلیکیشن تجمیع کند.</div>\r\n<div>نکته جالب در مورد این سیستم، داشتن یک دستیار شخصی مجازی است که مانند Siri به نظر می رسد. البته تفاوت هایی نیز وجود دارد. Siri داده ها را در اکوسیستم اختصاصی اپل جمع آوری و تجمیع می کند، در حالی که سامی پلتفرمی باز محسوب می شود. در حال حاضر، سامسونگ با حدود 50 شرکت مختلف از جمله Fitbit و Pebble در زمینه تست و توسعه فناوری های مرتبط با این پلتفرم همکاری می کند.\r\n<div> </div>\r\nسامسونگ در نظر دارد توسط این پلتفرم داده های مربوط به محصولات مختلف را نرمال سازی کرده و در قالب یک اپلیکیشن مدیریت کند. اما از آن جایی که تمرکز اصلی سامسونگ بر روی سخت افزار است، شاید در توسعه نرم افزار سامی با دشواری هایی روبرو شود. لوک جولیا در این مورد گفته که شاید سامسونگ فعلا در زمینه نرم افزار و سرویس ها دانش کافی نداشته باشد ولی دنبال ورود به این عرصه و ربودن گوی سبقت از رقبا است.</div>', '2013-11-11 23:49:33', 2, 'نارنجی', 17),
(62, 'عرضه آیفون هایی با نمایشگر بزرگتر و خمیده توسط اپل در فصل سوم 2014', '../newspics/iphone.jpg', '<p>بلومبرگ گزارش می کند که «منابعی آشنا به برنامه های اپل» می گویند این شرکت قصد دارد سال آینده دو آیفون جدید با نمایشگر خمیده را راهی بازار کند. این دو اسمارت فون از نمایشگرهای ۴.۷ اینچی و ۵.۵ اینچی بهره مند خواهند بود، که هر دو به شکل قابل توجهی از نمایشگر رتینا ۴ اینچی مورد استفاده در آیفون 5S بزرگتر هستند. و البته می توان آنها را رقبایی برای گلکسی اس ۴ یا نوت ۳ سامسونگ محسوب کرد.</p>\r\n<div>احتمال می رود که این دو تلفن جدید جایگزینهایی برای آیفون 5C و 5S باشند که سپتامبر امسال رونمایی شدند. بلومبرگ معتقد است که آیفون های جدید هم در فاصله زمانی مشابهی معرفی خواهند شد.</div>\r\n<div>برنامه ریزی انجام شده از عرضه هر دو مدل آیفون جدید در نیمه دوم سال آینده خبر می دهد. که قرار است نمایشگرهایی بزرگتر و شیشه ای خمیده به سمت لبه ها داشته باشند. منبع این اطلاعات می گوید هنوز جزئیات بیشتری درباره این محصولات عرضه نشده. به گفته وی، البته احتمال می رود سنسورهایی که سبکی و سنگینی لمس صفحه را تشخیص دهند به این نمایشگرها افزوده شوند.</div>\r\n<div>این گزارش همچنین شامل جزئیاتی درباره تلاش های اپل در زمینه سنسورهای حسگر فشار روی صفحه نمایشگر است. اگرچه حدس زده می شود چنین کارایی (تشخیص اینکه نمایشگر با چه میزان فشاری لمس شده) در مدل های ۲۰۱۴ آیفون گنجانده نشود و برنامه ای برای مدلهای بعدی باشد.</div>\r\n<div>مدتی است که شایعه آیفون هایی با نمایشگر بزرگتر بر سر زبانها است و پیش از این هم وال استریت ژورنال به نقل از DisplaySearch اندازه نمایشگرهایی منطبق بر خبر بلومبرگ را تایید کرده بود. البته بلومبرگ اولین منبع معتبری است که به احتمال خمیده بودن نمایشگر اشاره کرده است.</div>', '2013-11-11 11:22:55', 1, 'نارنجی', 17);

