INSERT INTO `slides` (`id`, `image`, `subject`, `body`, `pos`) VALUES
(5, '../slidespics/responsive-web.jpg', 'نمایش رسپانسیو سایت', 'امروزه صفحه نمایش ها از 320 پیکسل شروع شده و به 2560 پیکسل و حتی بزرگتر ختم میشود. بنابراین طرح های سنتی و قدیمی با عرض ثابت جوابگو نیست و طراحی های جدید نیاز به استفاده از روش انطباقی (Responsive) دارد.', 3),
(6, '../slidespics/seo.jpg', 'رتبه سایت', 'ممکن است که وب سایت یا وبلاگ شخصی داشته باشید که این روزها اکثر افراد دارند. در راه اندازی سایت، مراحل متعددی مانند طراحی، محتوا و بهینه سازی وجود دارند. آیا باید سایتتان را خودتان طراحی کنید، یا طراح متخصص استخدام کنید؟', 3),
(7, '../slidespics/browser.jpg', 'نمایش بهینه سایت', 'ده ها مرورگر وب در سراسر جهان در حال استفاده میباشند. برای همه آنها اجرای سندهای W3C کمی متفاوت است. طراحی سایت باید با این تفاوتها دست به گریبان باشد.', 3),
(8, '../slidespics/google-adwords.jpg', 'تبلیغات اینترنتی', 'توجه به این نکته بسیار مهم است که مادامی که سایت شما دیده نشود نمی تواند برای شما هیچگونه سودآوری داشته باشد. بنابراین زمانی شما می توانید از وب سایت خود کسب درآمد کنید که توسط مشتری بازدید شوید.', 3);

