INSERT INTO `works` (`id`, `subject`, `image`, `body`, `link`, `sdate`, `fdate`) VALUES
(107, 'شرکت تکنو پرینت', '../workspics/technoprint.jpg', '<p><span>عرضه کننده تجهیزات فروشگاهی نرم افزار های نقطه فروش مبتنی بر بارکد، این شرکت با یک دهه تجربه در این زمینه تامین کننده نیازهای اساسی صنف یا صنعت شما است . </span><br /><span>سیستمهای فروشگاهی لمسی - سیستمهای کنترل تردد وحضور غیاب - گیتهای امنیتی - پرینترهای چاپ لیبل و بارکد - پرینترهای صدور فیش - اسکنرهای بارکد بیسیم و سیم دار - نرم افزارهای فروشگاهی - نرم افزارهای لیبلینگ کالا و مدیریت انبار تحت بارکد</span></p>', 'technoprints.ir', '2013-02-18 11:39:53', '2013-03-14 11:39:53'),
(108, 'گروه موسیقی حنان', '../workspics/tehranipour.jpg', '<p>تنبور نوازان حنان (خراسان) از سال 87 با همدلی و همیاری نوازنده های تنبـــور به سرپرستی ابراهیم طهرانی پور تشکیل شد و تا به امروز دلهای نزدیک آنها به یکدیگر این گروه را منسجم تر از همیشه نگه داشته است . <br />ماهیت عرفانی گروه حیطه فعالیت حنان را در اشعار و غزلهای ناب حضرت مولانا و حافظ و دیگر شاعران و عارفان قرار داد تا با نغمه های اهورایی تنبور حالی دیگر از این منظومات شنیده شود . <br />خلق آثار جدید و باز آوری آثار ماندگار تنبور و تنبور نوازان نامدار مسیر حنان میباشد . خلق آثاری که هم از نظر بار موسیقیایی در برگیرنده ی مقامهای باستانی تنبور باشد و گریزی به موسیقی اصیل ایرانی زده باشد و هم از نظر مضامین شعری در برگیرنده مفاهیم عرفانی باشد تا مخاطب جان از سماع آن به وجد و فهم درآید . <br />تنبور نوازان حنان (خراسان) در این مدت توانست با برگزاری چند کنسرت در استان خراسان به یادآوری نغمه های تنبور و شناسایی این ساز در منطقه بپردازد و کنسرت موفقی را با هدف ترکیب تنبور کرمانشاهان و تنبور خراسان در آرامگاه حکیم فردوسی برگزار نماید .</p>', 'hananensemble.com/', '2013-02-16 17:49:32', '2013-03-18 17:49:32'),
(109, 'قالب شخصی مدیاتک (نمونه اول)', '../workspics/temp2.jpg', '<p>از نمونه های قالبهای طراحی شده توسط گروه طراحان مدیاتک می توان این قالب را معرفی کرد که قالبی راحت و روان برای بازدید کنندگان وب سایت می باشد.</p>\r\n<p>در این قالب تمامی اجزاء سایت در نگاه اول در اختیار بازدید کننده قرار گرفته و بازدیدکنندگان به هیچ عنوان دچار سردرگمی نخواهند شد.</p>\r\n<p>این وب سایت داری افکت هایی ویژه در عکسها و در قسمتهای مختلف سایت از جمله اخبار می باشد.</p>\r\n<p>محتوای وب سایت از طریق سیستم مدیریت محتوای مدیاتک قابل بارگذاری و تصحیح می باشد.</p>', 'demo1.mediateq.ir', '2013-06-26 17:24:26', '2013-07-24 17:24:26'),
(110, 'سیستم مدیریت محتوای مدیاتک', '../workspics/paneladmin1.jpg', '<p style="direction: rtl;">سیستم مدیریت محتوا (CMS) مدیاتک سیستمی <span>قدرتمند و یکپارچه را برای مؤلفین آثار فراهم می آورد. سیستم مدیریت محتوا (CMS) مدیاتک دارای محیطی ساده و روان برای درج کلیه خبرها، رزومه هاو... می باشد که قابلیت اضافه نمودن کلیه اسکریپت ها را برای کاربران با دسترسی مدیریت به صورت افزونه فراهم آورده است. همچنین این سیستم دارای نمودارهای نمایشی به صورت دایره ای، میله ای و... می باشد که برای تهیه یک سیستم آماری جهت بهتر ارائه نمودن یک فرایند برای بازدید کنندگان سایت مفید خواهد شد که می توان به صورت های مختلف از جمله فایلهای pdf، jpeg و ... از آن خروجی تهیه نمود.</span></p>\r\n<p style="direction: rtl;">از ویژیگی های دیگر این سیستم می توان به تعریف کاربران مختلف، ارسال خبرنامه به اعضاء، مدیریت قالب و قرار گرفتن بروی تمامی قالبهای طراحی شده (از جمله قالبهای شخصی، شرکتی و ...)، مدیریت فایلهای ارسال شده، نظرسنجی و ... نام برد.</p>\r\n<p style="direction: rtl;">این سیستم قابلیت اضافه نمودن تمامی قسمتهای مد نظر مشتریان عزیز را دارد . و از زمان شروع به کار تا این لحظه در حال بروز شدن با تمامی معیارهای استانداد در دنیای وب می باشد.</p>', '', '2013-05-11 17:35:37', '2013-10-07 17:35:37'),
(111, 'وب سایت شخصی آقای دکتر صدری', '../workspics/drsadri.png', '<p>این سایت متعلق به دکتر اقبال صدری متخصص ارتوپد می باشد.</p>\r\n<p>این سایت کاملا رسپانسیو می باشد. طراحی رسپانسیو به طراحی گفته می شود که در هنگام بازدید از سایت با توجه به دستگاه مورد استفاده و نوع سایز نمایش دستگاه، وب سایت نمایش داده می شود. اگر از موبایل یا تبلت استفاده می کنید بروی لینک زیر کلیک نمایید تا حالت رسپانسیو را بهتر درک نمایید..</p>\r\n<p>به دور زبان فارسی و انگلیسی طراحی شده است. دارای قسمتهای مختلف از جمله بخش رزومه، خدمات، گالری تصاویر و تماس با ما می باشد.</p>\r\n<p>طراحی سایت به اتمام رسیده است تنها بارگذاری اطلاعات آن توسط مدیریت وب سایت که از طریق سیستم مدیریت محتوای مدیاتک می باشد باقی مانده است.</p>', 'demo2.mediateq.ir', '2013-09-07 17:32:28', '2013-09-22 17:32:28'),
(112, 'قالب شخصی مدیاتک (نمونه دوم)', '../workspics/Mediateq.png', '<p>قالب شخصی که مشاهده می کنید قالبی با افکت های جدید می باشد.</p>\r\n<p>دارای قسمتهای مختلف از جمله نمایش عکس به صورت اسلاید، بخشی برای معرفی خدمات ، معرفی خود یا کار خود، گالری تصاویر و... می باشد.</p>\r\n<p><span>در این قالب تمامی اجزاء سایت در نگاه اول در اختیار بازدید کننده قرار گرفته و بازدیدکنندگان به هیچ عنوان دچار سردرگمی نخواهند شد.</span></p>\r\n<p><span><span>محتوای وب سایت از طریق سیستم مدیریت محتوای مدیاتک قابل بارگذاری و تصحیح می باشد.</span></span></p>', 'demo3.mediateq.ir', '2013-09-23 17:44:12', '2013-10-06 17:44:12');

